import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import axios from "axios";

const Brand = () => {
  const { brandId } = useParams(); // Get the brand ID from the URL
  const [brand, setBrand] = useState(null);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchBrand = async () => {
      console.log('Brand ID:', brandId); // Log the ID for validation
      try {
        const response = await axios.get(`/api/brands/${brandId}`); // Use ID here
        setBrand(response.data);
      } catch (err) {
        console.error(err); // Log error
        setError("Failed to load brand information");
      }
    };
  
    fetchBrand();
  }, [brandId]);
  

  if (error) {
    return <div>{error}</div>;
  }

  if (!brand) {
    return <div>Loading...</div>;
  }

  return (
    <div>
      <h1>{brand.name}</h1>
      <img src={brand.logoUrl} alt={brand.name} className="brand-logo" />
      <p>{brand.description}</p>
      <div className="brand-images">
        {brand.images.map((image, index) => (
          <img
            key={index}
            src={image}
            alt={`${brand.name} image ${index + 1}`}
            className="brand-image"
          />
        ))}
      </div>
    </div>
  );
};

export default Brand;
