import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Cards.css'; // Make sure to import your CSS for styling

const Cards = () => {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    // Fetch products from the backend (this would call your MongoDB)
    axios.get('/api/products')
      .then(response => setProducts(response.data))
      .catch(error => console.error("Error fetching products:", error));
  }, []);

  const handleAddToCart = (product) => {
    // Function to handle adding to cart (could call an API route to add product to user's cart in MongoDB)
    axios.post('/api/cart', { productId: product._id })
      .then(response => alert(`${product.name} added to cart!`))
      .catch(error => console.error("Error adding product to cart:", error));
  };

  return (
    <div className="favorites-container">
      {products.map((product) => (
        <div key={product._id} className="card">
          <img src={product.image} alt={product.name} className="card-img" />
          <div className="card-content">
            <h3 className="card-title">{product.name}</h3>
            <p className="card-price">${product.price}</p>
            <button className="btn-add-to-cart" onClick={() => handleAddToCart(product)}>
              Add to Cart
            </button>
          </div>
        </div>
      ))}
    </div>
  );
};

export default Cards;
