import React, { useState, useContext } from 'react'
import { UserContext } from "./UserContext";
import { Container, Form, Button, Alert } from 'react-bootstrap'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import Footer from '../Footer/Footer';
import NavigationBar from '../NavigationBar/NavigationBar';
import './LogIn.css'

const LogIn = () => {
    const { setUserInfo } = useContext(UserContext);
    const nav = useNavigate()
    const [userLog, setUserLog] = useState({
        email: "",
        password: ""
    })

    const handleChange = (e) => {
        setUserLog({ ...userLog, [e.target.name]: e.target.value })
    }

    const [error, setError] = useState("")
    const [alert, setAlert] = useState("")

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (!userLog.email) {
            setError('Email should not be empty');
            setAlert("danger")
            return;
        }
        if (userLog.password.length < 8) {
            setError('The password should have at least 8 characters!');
            setAlert("danger")
            return;
        }

        try {
            const res = await axios.post('http://localhost:5000/api/user/login/', userLog, {
                withCredentials: true
            });
            console.log(res.data);
            if (setUserInfo) {
                setUserInfo(res.data);
                nav('/')
            } else {
                console.error("setUserInfo is not a function");
                setError('An error occurred during login. Please try again.');
                setAlert('danger');
            }
        } catch (err) {
            console.error("User not login", err);
            setAlert('danger');
            if (err.response && err.response.status === 404 && err.response.data === 'User not found') {
                setError('Incorrect email. Please check your email and try again.');
            } else {
                setError('Login failed. Please check your credentials and try again.');
            }
        };
    };

    return (
        <div className="login-page">
            <NavigationBar />
            <Container className="login-container">
                <Form className="login-form" onSubmit={handleSubmit}>
                    <Form.Group className="login-form-group">
                        <Form.Label className="login-form-label">Email</Form.Label>
                        <Form.Control
                            className="login-form-control"
                            type="email"
                            onChange={handleChange}
                            name="email"
                            value={userLog.email}
                            placeholder="Enter email"
                        />
                    </Form.Group>
                    <Form.Group className="login-form-group">
                        <Form.Label className="login-form-label">Password</Form.Label>
                        <Form.Control
                            className="login-form-control"
                            type="password"
                            name="password"
                            onChange={handleChange}
                            value={userLog.password}
                            placeholder="Enter password"
                        />
                    </Form.Group>
                    <Button 
                        className="login-submit-btn" 
                        variant="primary" 
                        type="submit"
                        onClick={handleSubmit}
                    >
                        Login
                    </Button>
                </Form>
                <p className='login-register-link'>
                    Don't have an account? <a href="/register">Register</a>
                </p>
                <Alert variant={alert} className='login-alert'>
                    {error}
                </Alert>
            </Container>
            <Footer />
        </div>
    )
}

export default LogIn
