import React, { useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import ShoppingCartOutlinedIcon from "@mui/icons-material/ShoppingCartOutlined";
import "./NavigationBar.css";
import { Link } from 'react-router-dom';
import { Container, Nav, Navbar, Form, NavDropdown } from "react-bootstrap";

const NavigationBar = () => {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [products, setProducts] = useState([]);
  const [cartCount, setCartCount] = useState(0);


  // Function to toggle search bar visibility
  const toggleSearch = () => setIsSearchOpen(!isSearchOpen);

  // Function to handle search query and fetch products from the database
  const handleSearch = async (e) => {
    e.preventDefault();

    // Assuming you have an API endpoint to get products
    const response = await fetch(`/api/products?search=${searchQuery}`);
    const data = await response.json();

    // Set the fetched products to the state
    setProducts(data);
  };

  return (
    <>
      {/* Navbar Section */}
      <Navbar expand="lg" className="navbar">
  <Container fluid>
    <Navbar.Brand href="/" className="brand">
      DESIGNER.AL
    </Navbar.Brand>

    <Navbar.Toggle aria-controls="navbarScroll" />
    <Navbar.Collapse id="navbarScroll">
      <Nav className="me-auto">
        <Nav.Link href="/about">About Us</Nav.Link>
        <Nav.Link href="/contact">Contact Us</Nav.Link>
        <NavDropdown title="Explore" id="navbarScrollingDropdown">
          <NavDropdown.Item href="/products">Fragrances</NavDropdown.Item>
          <NavDropdown.Item href="/products">Accessories</NavDropdown.Item>
          <NavDropdown.Item href="/products">Hair Care</NavDropdown.Item>
          <NavDropdown.Item href="/products">Makeup</NavDropdown.Item>
          <NavDropdown.Item href="/products">Sets</NavDropdown.Item>
        </NavDropdown>
      </Nav>

      <Form className="d-flex" onSubmit={handleSearch}>
        <SearchIcon className="icon" onClick={toggleSearch} />
        {isSearchOpen && (
          <input
            type="text"
            placeholder="Search for products..."
            className="search-input"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
        )}
      </Form>

      <Link to="/cart" className="cart-icon">
        <ShoppingCartOutlinedIcon className="icon" />
        <span>{cartCount}</span> {/* Dynamic count */}
      </Link>
    </Navbar.Collapse>
  </Container>
</Navbar>


      {/* Product Section (Below Navbar) */}
      {isSearchOpen && (
        <div className="product-section">
          <div className="sidebar">
            <h2>Markat</h2>
            <ul>
              <li>MOSCHINO JEANS</li>
              <li>REPLAY</li>
              <li>GUESS</li>
              <li>HUGO</li>
              <li>Calvin Klein</li>
            </ul>
          </div>

          {/* Products Section */}
          <div className="products">
            <div className="product">
              <img src="path_to_image" alt="Scotch & Soda Polo" />
              <p>Scotch&Soda - Bluzë polo e bardhë për meshkuj</p>
              <span>7,390.00 Lek</span>
            </div>

            <div className="product">
              <img src="path_to_image" alt="HUGO Shirt" />
              <p>HUGO - Këmishë për meshkuj me printim logo</p>
              <span>9,990.00 Lek</span>
            </div>

            <div className="product">
              <img src="path_to_image" alt="Guess Jacket" />
              <p>Guess - Xhaketë bezhë për meshkuj</p>
              <span>17,490.00 Lek</span>
            </div>

            <div className="product">
              <img src="path_to_image" alt="Calvin Klein Bag" />
              <p>Calvin Klein - Çantë femrash me zinxhir</p>
              <span>10,000.00 Lek</span>
            </div>

            <div className="product">
              <img src="path_to_image" alt="Guess Dress" />
              <p>Guess - Fustan sateni me logo</p>
              <span>12,000.00 Lek</span>
            </div>

            <div className="product">
              <img src="path_to_image" alt="Calvin Klein Sneakers" />
              <p>Calvin Klein - Atlete të bardha për meshkuj</p>
              <span>8,490.00 Lek</span>
            </div>
          </div>
        </div>
      )}
    </>
  );
};

export default NavigationBar;
