import React, { useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import ShoppingCartOutlinedIcon from "@mui/icons-material/ShoppingCartOutlined";
import "./NavigationBar.css"; 
import { Container, Nav, Navbar, Form } from "react-bootstrap";

const NavigationBar = () => {
  const [activeMenu, setActiveMenu] = useState(null);

  return (
    <Navbar expand="lg" className="navbar">
      <Container fluid>
        <Navbar.Brand href="/" className="brand">FASHION&FRIENDS</Navbar.Brand>

        <Nav className="me-auto my-2 my-lg-0">
          <Nav.Link
            onMouseEnter={() => setActiveMenu("femra")}
            onMouseLeave={() => setActiveMenu(null)}
          >
            FEMRA
          </Nav.Link>
          <Nav.Link
            onMouseEnter={() => setActiveMenu("meshkuj")}
            onMouseLeave={() => setActiveMenu(null)}
          >
            MESHKUJ
          </Nav.Link>
        </Nav>

        <Form className="d-flex">
          <SearchIcon className="icon" />
          <div className="cart-icon">
            <ShoppingCartOutlinedIcon className="icon" />
            <span>0</span>
          </div>
        </Form>

        {/* Dropdown Menus */}
        <div
          className={`dropdown-content ${activeMenu === "femra" ? "show" : ""}`}
          onMouseEnter={() => setActiveMenu("femra")}
          onMouseLeave={() => setActiveMenu(null)}
        >
          <ul>
            <li><a href="/femra/kepuce">Këpucë</a></li>
            <li><a href="/femra/veshje">Veshje</a></li>
          </ul>
          <div className="dropdown-image">
            <img src="/path/to/femra.jpg" alt="Femra" />
          </div>
        </div>

        <div
          className={`dropdown-content ${activeMenu === "meshkuj" ? "show" : ""}`}
          onMouseEnter={() => setActiveMenu("meshkuj")}
          onMouseLeave={() => setActiveMenu(null)}
        >
          <ul>
            <li><a href="/meshkuj/kepuce">Këpucë</a></li>
            <li><a href="/meshkuj/veshje">Veshje</a></li>
          </ul>
          <div className="dropdown-image">
            <img src="/path/to/meshkuj.jpg" alt="Meshkuj" />
          </div>
        </div>
      </Container>
    </Navbar>
  );
};

export default NavigationBar;
