import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';

const Brand = () => {
  const { brandName  } = useParams(); // Get the brand ID from the URL parameters
  const [brand, setBrand] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchBrand = async () => {
      try {
        const response = await axios.get(`/api/brands/${brandName}`);
        setBrand(response.data);
      } catch (err) {
        setError('Failed to load brand information');
      }
    };

    fetchBrand();
  }, [id]);

  if (error) {
    return <div>{error}</div>;
  }

  if (!brand) {
    return <div>Loading...</div>; // Loading state
  }

  return (
    <div>
      <h1>{brand.name}</h1>
      <img src={brand.logoUrl} alt={brand.name} className="brand-logo" />
      <p>{brand.description}</p>
      {/* Display the array of images */}
      <div className="brand-images">
        {brand.images.map((image, index) => (
          <img key={index} src={image} alt={`${brand.name} image ${index + 1}`} className="brand-image" />
        ))}
      </div>
    </div>
  );
};

export default Brand;
