import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';

const UserProfile = () => {
    const [userInfo, setUserInfo] = useState(null); 
    const navigate = useNavigate();

    useEffect(() => {
        const storedUserInfo = localStorage.getItem('userInfo');
        if (storedUserInfo) {
            setUserInfo(JSON.parse(storedUserInfo)); 
        } else {
            navigate('/login'); // Redirect to login if no user info is found
        }
    }, [navigate]);

    const handleLogout = () => {
        // Clear local storage
        localStorage.removeItem('userInfo');
        localStorage.removeItem('token');
        
        // Update the logged-in state
        setUserInfo(null); // Clear user info
        
        // Redirect to login page
        navigate('/login'); // Adjust the route as needed
    };

    return (
        <div className="profile-container">
            {userInfo ? (
                <div>
                    <h2>User Profile</h2>
                    <p><strong>Username:</strong> {userInfo.username}</p> {/* Display username */}
                    <p><strong>Email:</strong> {userInfo.email}</p> {/* Display email */}
                    {/* Add more user details here if needed */}
                    <button onClick={handleLogout}>Logout</button>
                </div>
            ) : (
                <p>Loading...</p> // Optional loading state
            )}
        </div>
    );
};

export default UserProfile;
