import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import axios from 'axios';
import './OneProduct.css';

const OneProduct = () => {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const { category, subCategory, id } = useParams();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        console.log(`Fetching product with category: ${category}, subCategory: ${subCategory}, id: ${id}`);
        const url = `/api/products/${category}/${subCategory}/${id}`;
        console.log('Requesting URL:', url);
        const response = await axios.get(url);
        console.log('API response:', response.data);
        setProduct(response.data);
        setLoading(false);
      } catch (err) {
        console.error('Error fetching product:', err);
        if (err.response) {
          console.error('Error response status:', err.response.status);
          console.error('Error response data:', err.response.data);
        } else if (err.request) {
          console.error('No response received:', err.request);
        } else {
          console.error('Error setting up request:', err.message);
        }
        setError(`Failed to fetch product: ${err.message}`);
        setLoading(false);
      }
    };

    fetchProduct();
  }, [category, subCategory, id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;
  if (!product) return <div>Product not found</div>;

  return (
    <div className="product-container">
      <div className="product-image">
        <img src={product.image} alt={product.name} />
      </div>
      <div className="product-info">
        <h1>{product.name}</h1>
        <p className="product-description">{product.description}</p>
        <p className="product-ref">Ref. {product.ref}</p>
        <p className="product-price">${product.price.toFixed(2)}*</p>
        <p className="product-dimensions">
          {product.dimensions.width} x {product.dimensions.height} x {product.dimensions.depth} in ( cm )
        </p>
        <div className="variants">
          <h3>VARIANTS ({product.variants.length})</h3>
          <div className="variant-options">
            {product.variants.map((variant, index) => (
              <div key={index} className="variant-option">
                <img src={variant.image} alt={`Variant ${index + 1}`} />
              </div>
            ))}
          </div>
        </div>
        <button className="contact-advisor-btn">CONTACT AN ADVISOR</button>
        <div className="discover-more">
          <a href="#">DISCOVER MORE CREATIONS IN BOUTIQUES</a>
          <div className="info-icon">?</div>
        </div>
        <p className="suggested-price">
          *Suggested retail price. <a href="#">More information</a>
        </p>
      </div>
    </div>
  );
};

export default OneProduct;
