import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Cards.css";
import { Link } from "react-router-dom";

const Cards = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    setLoading(true);
    axios
      .get("http://localhost:5000/api/products/least-quantity")
      .then((response) => {
        if (response.data && Array.isArray(response.data) && response.data.length > 0) {
          setProducts(response.data.slice(0, 4));
        } else {
          setError("No products with low quantity available.");
          setProducts([]);
        }
      })
      .catch((error) => {
        console.error("Error fetching products:", error);
        setError("Failed to fetch products. Please try again later.");
        setProducts([]);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);

  // const defaultProducts = [
  //   {
  //     _id: "default1",
  //     name: "Default 1",
  //     price: 10.0,
  //     image: "https://via.placeholder.com/300",
  //   },
  //   {
  //     _id: "default2",
  //     name: "Default 2",
  //     price: 20.0,
  //     image: "https://via.placeholder.com/300",
  //   },
  //   {
  //     _id: "default3",
  //     name: "Default 3",
  //     price: 30.0,
  //     image: "https://via.placeholder.com/300",
  //   },
  //   {
  //     _id: "default4",
  //     name: "Default 4",
  //     price: 76.0,
  //     image: "https://via.placeholder.com/300",
  //   },
  // ];

  const handleAddToCart = (product) => {
    axios
      .post("/api/cart", { productId: product._id })
      .then(() => alert(`${product.name} added to cart!`))
      .catch((error) => console.error("Error adding product to cart:", error));
  };

  if (loading) {
    return <div>Loading...</div>;
  }

  if (error) {
    return <div className="error-message">{error}</div>;
  }

  return (
    <div>
      <h3 className="favorites-heading">Last Chance to Buy</h3>
      {products.length > 0 ? (
        <div className="favorites-container">
          {products.map((product) => (
            <div key={product._id} className="card">
              <Link to={`/products/${product._id}`} className="card-link">
                <img
                  src={product.image || "https://via.placeholder.com/300"}
                  alt={product.name || "Default Product"}
                  className="card-img"
                />
              </Link>

              <div className="card-content">
                <h3 className="card-title">
                  <Link to={`/products/${product._id}`} className="card-link">
                    {product.name || "Name Unavailable"}
                  </Link>
                </h3>
                <p className="card-price">
                  ${product.price?.toFixed(2) || "0.00"}
                </p>
                <button
                  className="btn-add-to-cart"
                  onClick={() => handleAddToCart(product)}
                >
                  Add to Cart
                </button>
              </div>
            </div>
          ))}
        </div>
      ) : (
        <p>No products with low quantity available at the moment.</p>
      )}
    </div>
  );
};

export default Cards;
