import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import './UserProfile.css';

const UserProfile = () => {
    const [userInfo, setUserInfo] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const storedUserInfo = localStorage.getItem('userInfo');
        if (storedUserInfo) {
            setUserInfo(JSON.parse(storedUserInfo));
        } else {
            navigate('/login');
        }
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem('userInfo');
        localStorage.removeItem('token');
        setUserInfo(null);
        navigate('/login');
    };

    return (
        <div className="profile-container">
            {userInfo ? (
                <div className="profile-card">
                    <div className="profile-header">
                        <img src={userInfo.avatar || 'https://via.placeholder.com/150'} alt="Profile" className="profile-avatar" />
                        <h2>{userInfo.username}</h2>
                        <p className="user-title">{userInfo.title || 'Software Developer'}</p>
                    </div>
                    <div className="profile-info">
                        <p><strong>Email:</strong> {userInfo.email}</p>
                        <p><strong>Location:</strong> {userInfo.location || 'San Francisco, CA'}</p>
                        <p><strong>Joined:</strong> {userInfo.joinDate || 'January 1, 2023'}</p>
                        <p><strong>Bio:</strong> {userInfo.bio || 'Passionate about creating amazing web applications and solving complex problems.'}</p>
                    </div>
                    <div className="profile-stats">
                        <div className="stat">
                            <span className="stat-value">{userInfo.posts || 42}</span>
                            <span className="stat-label">Posts</span>
                        </div>
                        <div className="stat">
                            <span className="stat-value">{userInfo.followers || 1337}</span>
                            <span className="stat-label">Followers</span>
                        </div>
                        <div className="stat">
                            <span className="stat-value">{userInfo.following || 123}</span>
                            <span className="stat-label">Following</span>
                        </div>
                    </div>
                    <button onClick={handleLogout} className="logout-button">Logout</button>
                </div>
            ) : (
                <p>Loading...</p>
            )}
        </div>
    );
};

export default UserProfile;
