import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Products.css";
import { useParams, Link } from "react-router-dom";
import NavigationBar from "../NavigationBar/NavigationBar";
import Footer from "../Footer/Footer";
import { v4 as uuidv4 } from "uuid";

const Products = () => {
  const { category, subCategory } = useParams(); // Add subCategory to useParams
  const [products, setProducts] = useState([]);
  const [priceRange, setPriceRange] = useState(500);
  const [selectedColors, setSelectedColors] = useState([]);
  const [cart, setCart] = useState([]);
  const [selectedBrands, setSelectedBrands] = useState([]);
  const [availableBrands, setAvailableBrands] = useState([]);
  const [cartItemsCount, setCartItemsCount] = useState(0);

  useEffect(() => {
    useEffect(() => {
      const fetchProducts = async () => {
        try {
          let endpoint = "http://localhost:5000/api/products";
          if (category) {
            endpoint += `/${category}`;
          }
          if (subCategory) {
            endpoint += `/${subCategory}`; // Append subcategory to the endpoint if available
          }
          const response = await axios.get(endpoint);
          setProducts(response.data);
    
          // Extract unique brands from products
          const brands = [
            ...new Set(response.data.map((product) => product.brand)),
          ];
          setAvailableBrands(brands);
        } catch (error) {
          console.error("Error fetching products", error);
        }
      };
      fetchProducts();
    }, [category, subCategory]); // Re-fetch products when either category or subcategory changes
     const fetchProducts = async () => {
      try {
        let endpoint = "http://localhost:5000/api/products";
        if (category) endpoint += `/${category}`;
        const response = await axios.get(endpoint);
        setProducts(response.data);

        // Extract unique brands from products
        const brands = [
          ...new Set(response.data.map((product) => product.brand)),
        ];
        setAvailableBrands(brands);
      } catch (error) {
        console.error("Error fetching products", error);
      }
    };
    fetchProducts();
    // fetchCart();
  }, [category]);

  const handlePriceRangeChange = (event) => setPriceRange(event.target.value);

  const handleBrandChange = (brand) => {
    setSelectedBrands((prevBrands) =>
      prevBrands.includes(brand)
        ? prevBrands.filter((b) => b !== brand)
        : [...prevBrands, brand]
    );
  };

  const getUserId = () => {
    let userId = localStorage.getItem("userId");
    if (!userId) {
      userId = uuidv4();
      localStorage.setItem("userId", userId);
    }
    return userId;
  };

  const addToCart = async (productId, quantity) => {
    const userId = getUserId();
    try {
      await axios.post(`http://localhost:5000/api/cart/${userId}/add`, {
        productId,
        quantity,
      });
      // fetchCart(); // Fetch the updated cart
    } catch (error) {
      console.error("Error adding to cart", error);
    }
  };
  
  // const fetchCart = async () => {
  //   const userId = getUserId();
  //   try {
  //     const response = await axios.get(
  //       `http://localhost:5000/api/cart/${userId}`
  //     );
  //     setCart(response.data.items || []);
  //     setCartItemsCount(
  //       response.data.items.reduce((total, item) => total + item.quantity, 0)
  //     );
  //   } catch (error) {
  //     console.error("Error fetching cart", error);
  //   }
  // };
  

  const totalItemsInCart = cart.reduce(
    (total, item) => total + item.quantity,
    0
  );

  const applyFilters = () => {
    const filteredProducts = products.filter(
      (product) =>
        product.price <= priceRange &&
        (selectedColors.length === 0 ||
          selectedColors.includes(product.color)) &&
        (selectedBrands.length === 0 || selectedBrands.includes(product.brand))
    );
    return filteredProducts;
  };

  const filteredProducts = applyFilters();

  return (
    <div className="products-page">
      <NavigationBar cartItemsCount={cartItemsCount} />
      <div className="container">
        <aside className="filters-section">
          <h2 className="filters-title">Refine Your Search</h2>
          <div className="filter-group">
            <h3 className="filter-group-title">Price Range</h3>
            <input
              type="range"
              min="0"
              max="5000"
              value={priceRange}
              onChange={handlePriceRangeChange}
              className="price-slider"
            />
            <p className="price-range-display">Up to ${priceRange}</p>
          </div>
          <div className="filter-group">
            <h3 className="filter-group-title">Brands</h3>
            <div className="brand-options">
              {availableBrands.map((brand) => (
                <label key={brand} className="brand-option">
                  <input
                    type="checkbox"
                    checked={selectedBrands.includes(brand)}
                    onChange={() => handleBrandChange(brand)}
                  />
                  {brand}
                </label>
              ))}
            </div>
          </div>
        </aside>
        <section className="product-grid">
          <div className="product-grid-header">
            <h2 className="product-grid-title">Discover Our Collection</h2>
          </div>
          <div className="products-list">
            {filteredProducts.map((product) => {
              // Filter out "new" and "sale" from category and subCategory
              const filteredCategory = Array.isArray(product.category)
                ? product.category.filter(
                    (cat) => cat !== "new" && cat !== "sale"
                  )
                : [product.category].filter(
                    (cat) => cat !== "new" && cat !== "sale"
                  );

              const filteredSubCategory = Array.isArray(product.subCategory)
                ? product.subCategory.filter(
                    (sub) => sub !== "new" && sub !== "sale"
                  )
                : [product.subCategory].filter(
                    (sub) => sub !== "new" && sub !== "sale"
                  );

              // Construct the URL path
              const urlPath = [
                ...filteredCategory,
                ...filteredSubCategory,
                product._id,
              ].join("/");

              return (
                <div className="product-card" key={product._id}>
                  <Link to={`/products/${urlPath}`} className="product-link">
                    <img
                      src={product.image}
                      alt={product.name}
                      className="product-image"
                    />
                    <div className="product-info">
                      <h3 className="product-name">{product.name}</h3>
                      <p className="product-price">
                        ${product.price.toFixed(2)}
                      </p>
                      <p className="product-brand">{product.brand}</p>
                    </div>
                  </Link>
                  <button
                    className="add-to-cart-btn"
                    onClick={() => addToCart(product._id, 1)}
                  >
                    Add to Cart
                  </button>
                </div>
              );
            })}
          </div>
        </section>
      </div>
      <Footer />
    </div>
  );
};

export default Products;
