import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "./OneProduct.css";
import Footer from "../Footer/Footer";
import NavigationBar from "../NavigationBar/NavigationBar";
import Slider from "react-slick";

const OneProduct = () => {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [suggestions, setSuggestions] = useState([]);
  const { category, subCategory, id } = useParams();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const baseUrl =
          process.env.REACT_APP_API_URL || "http://localhost:5000";
        const url = `${baseUrl}/api/products/${category}/${subCategory}/${id}`;
        const response = await axios.get(url);
        setProduct(response.data);
        setLoading(false);
      } catch (err) {
        setError(`Failed to fetch product: ${err.message}`);
        setLoading(false);
      }
    };

    fetchProduct();
  }, [category, subCategory, id]);

  useEffect(() => {
    setLoading(true);
    axios
      .get("http://localhost:5000/api/products/suggestions", {
        params: { limit: 4 }, // Limit the number of products to 4
      })
      .then((response) => {
        if (
          response.data &&
          Array.isArray(response.data) &&
          response.data.length > 0
        ) {
          // Set random products from the response
          setProducts(response.data);
        } else {
          setError("No products available.");
          setProducts([]);
        }
      })
      .catch((error) => {
        console.error("Error fetching products:", error);
        setError("Failed to fetch products. Please try again later.");
        setProducts([]);
      })
      .finally(() => {
        setLoading(false);
      });
  }, []);
  
  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;
  if (!product) return <div>Product not found</div>;


  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };


  return (
    <div className="one-product-page">
      <NavigationBar />
      <div className="product-details">
        <img src={product.image} alt={product.name} className="product-image" />
        <div className="product-info">
          <h1 className="product-name">{product.name}</h1>
          <p className="product-price">${product.price.toFixed(2)}</p>
          <p className="product-description">{product.description}</p>
          <div className="quantity-controls">
            <button onClick={() => setQuantity(Math.max(quantity - 1, 1))}>-</button>
            <span>{quantity}</span>
            <button onClick={() => setQuantity(quantity + 1)}>+</button>
          </div>
          <button className="add-to-cart-btn">
            Add to Cart
          </button>
        </div>
      </div>

      <section className="suggestions-section">
        <h2>You May Also Like</h2>
        <Slider {...carouselSettings}>
          {suggestions.length > 0 ? (
            suggestions.map((randomProduct) => (
              <div key={randomProduct._id} className="suggestion-item">
                <Link to={`/products/${randomProduct.category}/${randomProduct.subCategory}/${randomProduct._id}`}>
                <img src={randomProduct.productImage} alt={randomProduct.productName} className="suggestion-image" />
                <h3>{randomProduct.name}</h3>
                  <p>${randomProduct.price.toFixed(2)}</p>
                </Link>
              </div>
            ))
          ) : (
            <p>No suggestions found</p>
          )}
        </Slider>

      </section>

      <Footer />
    </div>
  );
};

export default OneProduct;
