import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Container, Row, Col, Image } from "react-bootstrap";
import "./MainGrid.css";
import axios from "axios";

const MainGrid = () => {
  const [brands, setBrands] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchBrands = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/brands");
        setBrands(response.data);
      } catch (err) {
        setError("Failed to load brands");
      }
    };

    fetchBrands();
  }, []);

  const getRandomBrands = (brandsArray, num) => {
    const shuffled = brandsArray.sort(() => 0.5 - Math.random());
    return shuffled.slice(0, num);
  };

  if (error) {
    return <div>{error}</div>;
  }

  const randomBrands = getRandomBrands(brands, 4); // Get 4 random brands

  return (
    <Container className="mt-4 image-grid">
      <Row>
        <Col md={6} className="left-image">
          <Link to={`/brands/${randomBrands[0]._id}`} className="image-link">
            <Image
              src={randomBrands[0].images[0]} // Using the first image as the display image
              alt={randomBrands[0].name}
              fluid
              className="big-image mb-3"
            />
          </Link>
        </Col>
        <Col md={6} className="right-images">
          <Row>
            <Col md={6} className="top-image">
              <Link to={`/brands/${randomBrands[1]._id}`} className="image-link">
                <Image
                  src={randomBrands[1].images[0]}
                  alt={randomBrands[1].name}
                  fluid
                  className="small-image mb-3"
                />
              </Link>
            </Col>
            <Col md={6} className="top-image">
              <Link to={`/brands/${randomBrands[2]._id}`} className="image-link">
                <Image
                  src={randomBrands[2].images[0]}
                  alt={randomBrands[2].name}
                  fluid
                  className="small-image mb-3"
                />
              </Link>
            </Col>
          </Row>
          <Row>
            <Col md={12} className="bottom-image">
              <Link to={`/brands/${randomBrands[3]._id}`} className="image-link">
                <Image
                  src={randomBrands[3].images[0]}
                  alt={randomBrands[3].name}
                  fluid
                  className="small-image"
                />
              </Link>
            </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default MainGrid;
