import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Products.css";
import { useParams, Link } from "react-router-dom";
import NavigationBar from "../NavigationBar/NavigationBar";
import Footer from "../Footer/Footer";
import { v4 as uuidv4 } from "uuid";

const Products = () => {
  const { category, subCategory } = useParams();
  const [products, setProducts] = useState([]);
  const [priceRange, setPriceRange] = useState(500);
  const [selectedColors, setSelectedColors] = useState([]);
  const [availableColors] = useState([
    "red",
    "blue",
    "green",
    "black",
    "white",
  ]);
  const [cart, setCart] = useState([]);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        let endpoint = "http://localhost:5000/api/products";
        if (category) {
          endpoint += `/${category}`;
        }
        if (subCategory) {
          endpoint += `/${subCategory}`;
        }
        const response = await axios.get(endpoint);
        setProducts(response.data);
      } catch (error) {
        console.error("Error fetching products", error);
      }
    };
    fetchProducts();
  }, [category, subCategory]);

  const handlePriceRangeChange = (event) => {
    setPriceRange(event.target.value);
  };

  const handleColorChange = (color) => {
    setSelectedColors((prevColors) =>
      prevColors.includes(color)
        ? prevColors.filter((c) => c !== color)
        : [...prevColors, color]
    );
  };

  const getUserId = () => {
    // Check if userId already exists in localStorage
    let userId = localStorage.getItem("userId");

    // If not, create a new user ID
    if (!userId) {
      userId = uuidv4(); // Generate a unique ID using uuid
      localStorage.setItem("userId", userId);
    }

    return userId;
  };

  const addToCart = async (productId, quantity) => {
    const userId = getUserId(); // Get the generated user ID
    await fetch("/api/cart/add", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify({ userId, productId, quantity }),
    });
  };

  const totalItemsInCart = cart.reduce(
    (total, item) => total + item.quantity,
    0
  );

  const filteredProducts = products.filter(
    (product) =>
      product.price <= priceRange &&
      (selectedColors.length === 0 || selectedColors.includes(product.color))
  );

  return (
    <div>
      <NavigationBar />
      <div className="container">
        <aside className="filters-section">
          <h2>Filters</h2>
          <div className="filter-group">
            <h3>Price Range</h3>
            <input
              type="range"
              min="0"
              max="1000"
              value={priceRange}
              onChange={handlePriceRangeChange}
            />
            <p>Up to ${priceRange}</p>
          </div>
          <div className="filter-group">
            <h3>Colors</h3>
            <div className="color-options">
              {availableColors.map((color) => (
                <div
                  key={color}
                  className={`color-option ${
                    selectedColors.includes(color) ? "selected" : ""
                  }`}
                  style={{ backgroundColor: color }}
                  onClick={() => handleColorChange(color)}
                ></div>
              ))}
            </div>
          </div>
          <button className="apply-filters">Apply Filters</button>
        </aside>
        <section className="product-grid">
          <h2>All Products</h2>
          <div className="cart-info">
            <p>Cart: {totalItemsInCart} items</p>
          </div>
          <div className="products-list">
            {filteredProducts.map((product) => (
              <div className="product-card" key={product._id}>
                <Link
                  to={`/products/${category}/${subCategory}/${product._id}`}
                ></Link>
                <img src={product.image} alt={product.name} />
                <div className="product-info">
                  <h3>{product.name}</h3>
                  <p>${product.price}</p>
                  <button
                    className="buy-now"
                    onClick={() => addToCart(product)}
                  >
                    Buy Now
                  </button>
                </div>
              </div>
            ))}
          </div>
        </section>
      </div>
      <Footer />
    </div>
  );
};

export default Products;
