import React, { useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import ShoppingCartOutlinedIcon from "@mui/icons-material/ShoppingCartOutlined";
import "./NavigationBar.css"; // Make sure you include the CSS changes
import { Container, Nav, Navbar, Form, NavDropdown } from "react-bootstrap";

const NavigationBar = () => {
  const [isNavbarOpen, setIsNavbarOpen] = useState(false);

  // Function to toggle navbar visibility
  const toggleNavbar = () => setIsNavbarOpen(!isNavbarOpen);

  const [activeMenu, setActiveMenu] = useState(null); // to track hover over women/men
  const [clickedMenu, setClickedMenu] = useState(null); // to handle click events
  const [hoverCategory, setHoverCategory] = useState(null); // to track hover over categories

  const handleMenuClick = (menu) => {
    if (clickedMenu === menu) {
      setClickedMenu(null);
    } else {
      setClickedMenu(menu);
    }
  };

  return (
    <Navbar expand="lg" className="navbar">
      <Container fluid>
        <Navbar.Brand href="/" className="brand">
          DESIGNER.AL
        </Navbar.Brand>

        <button className="navbar-toggler" type="button" onClick={toggleNavbar}>
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className={`navbar-collapse ${isNavbarOpen ? "show" : ""}`}>
          {/* Close button */}
          <span className="close-btn" onClick={toggleNavbar}>
            Ã—
          </span>

          <Nav className="me-auto my-2 my-lg-0">
            <Nav.Link href="/about">About Us</Nav.Link>
            <Nav.Link href="/contact">Contact Us</Nav.Link>
            <Nav.Link
              onMouseEnter={() => setActiveMenu("women")}
              onMouseLeave={() => setActiveMenu(null)}
              onClick={() => handleMenuClick("women")}
            >
              WOMEN'S
            </Nav.Link>
            <Nav.Link
              onMouseEnter={() => setActiveMenu("men")}
              onMouseLeave={() => setActiveMenu(null)}
              onClick={() => handleMenuClick("men")}
            >
              MEN'S
            </Nav.Link>

          <NavDropdown
              title="Explore"
              id="navbarScrollingDropdown"
              className="nav-dropdown"
            >
              <NavDropdown.Item href="/products" className='dropdown-item'>Fragrances</NavDropdown.Item>
              <NavDropdown.Item href="/products" className='dropdown-item'>Accessories</NavDropdown.Item>
              <NavDropdown.Item href="/products" className='dropdown-item'>Hair Care</NavDropdown.Item>
              <NavDropdown.Item href="/products" className='dropdown-item'>Makeup</NavDropdown.Item>
              <NavDropdown.Item href="/products">Sets</NavDropdown.Item>
            </NavDropdown>
          </Nav>

          <Form className="d-flex">
            <SearchIcon className="icon" />
            <div className="cart-icon">
              <ShoppingCartOutlinedIcon className="icon" />
              <span>0</span>
            </div>
          </Form>
        </div>
      </Container>
    </Navbar>
  );
};

export default NavigationBar;
