import React, { useState } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Footer from "../Footer/Footer";
import NavigationBar from "../NavigationBar/NavigationBar";
import "./LogIn.css";

const LogIn = () => {
  const navigate = useNavigate();
  const [userLog, setUserLog] = useState({
    email: "",
    password: "",
  });

  const [error, setError] = useState("");
  const [alert, setAlert] = useState("");

  const handleChange = (e) => {
    setUserLog({ ...userLog, [e.target.name]: e.target.value });
  };

  const handleLogin = async () => {
    try {
        const response = await axios.post('http://localhost:5000/api/login', { email, password });
        const { id, email, token } = response.data; // Destructure token from response
        
        // Store user info and token in local storage
        localStorage.setItem('userInfo', JSON.stringify({ id, email }));
        localStorage.setItem('token', token); // Store token
        
        // Redirect to profile or home page
        navigate('/profile'); 
    } catch (error) {
        console.error('Login failed:', error);
        alert('Login failed. Please check your credentials.');
    }
};


  return (
    <div className="login-page">
      <NavigationBar />
      <Container className="login-container">
        <Form className="login-form" onSubmit={handleSubmit}>
          <Form.Group className="login-form-group">
            <Form.Label className="login-form-label">Email</Form.Label>
            <Form.Control
              className="login-form-control"
              type="email"
              onChange={handleChange}
              name="email"
              value={userLog.email}
              placeholder="Enter email"
            />
          </Form.Group>
          <Form.Group className="login-form-group">
            <Form.Label className="login-form-label">Password</Form.Label>
            <Form.Control
              className="login-form-control"
              type="password"
              name="password"
              onChange={handleChange}
              value={userLog.password}
              placeholder="Enter password"
            />
          </Form.Group>
          <Button
            className="login-submit-btn"
            variant="primary"
            type="submit"
            onClick={handleSubmit}
          >
            Login
          </Button>
        </Form>
        <p className="login-register-link">
          Don't have an account? <a href="/register">Register</a>
        </p>
        <Alert variant={alert} className="login-alert">
          {error}
        </Alert>
      </Container>
      <Footer />
    </div>
  );
};

export default LogIn;
