import React, { useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import PersonOutlineIcon from "@mui/icons-material/PersonOutline";
import ShoppingCartOutlinedIcon from "@mui/icons-material/ShoppingCartOutlined";
import "./NavigationBar.css";
import {
  Container,
  Button,
  Nav,
  Navbar,
  Form,
  NavDropdown,
} from "react-bootstrap";
import About from "./About";

const NavigationBar = () => {
  const [searchBar, setSearchBar] = useState(false);

  const toggleSearch = () => {
    setSearchBar(!searchBar);
  };

  const [brand, setBrand] = useState(true);
  const toggleBrand = () => {
    setBrand(!brand);
  };

  return (
    <Navbar expand="lg" className="bg-body-tertiary navbar">
      <Container fluid>
        <Navbar.Toggle aria-controls="navbarScroll" onClick={toggleBrand} />
        <Navbar.Collapse id="navbarScroll">
          <Nav
            className="me-auto my-2 my-lg-0"
            style={{ maxHeight: "100px" }}
            navbarScroll
          >
            <Nav.Link href="#action1">About Us</Nav.Link>
            <Nav.Link href="#action2">Contact Us</Nav.Link>
            <NavDropdown title="Explore" id="navbarScrollingDropdown">
              <NavDropdown.Item href="#action3">Fragrances</NavDropdown.Item>
              <NavDropdown.Item href="#action4">Accessories </NavDropdown.Item>
              <NavDropdown.Item href="#action5">Hair Care </NavDropdown.Item>
              <NavDropdown.Item href="#action5">Makeup </NavDropdown.Item>
              <NavDropdown.Item href="#action5">Sets </NavDropdown.Item>
            </NavDropdown>
          </Nav>
          {brand && (
            <Navbar.Brand href="#" className="brand">
              DESIGNER.AL
            </Navbar.Brand>
          )}
          <Form className="d-flex">
            {searchBar && (
              <Form.Control
                type="search"
                placeholder="Search"
                className="me-2"
                aria-label="Search"
              />
            )}
            <SearchIcon onClick={toggleSearch} className="icon" />
            <PersonOutlineIcon className="icon" />
            <div className="cart-icon">
              <ShoppingCartOutlinedIcon className="icon" />
              <span>0</span>
            </div>
          </Form>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default NavigationBar;
