import React, {useE} from "react";
import {Link} from 'react-router-dom';
import { Container, Row, Col, Image } from "react-bootstrap";
import "./MainGrid.css";
import axios from "axios";

const MainGrid = () => {

  const [brands, setBrands] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchBrands = async () => {
      try {
        const response = await axios.get('/api/brands'); // Adjust endpoint as necessary
        setBrands(response.data);
      } catch (err) {
        setError("Failed to load brands");
      }
    };

    fetchBrands();
  }, []);

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <Container className="mt-4 image-grid">
      <Row>
        <Col md={6} className="left-image custom-col">
        <Link to={`/brands/${brand._id}`} className="image-link">
  <Image
    src="https://www.fashionandfriends.com/pub/media/wysiwyg/Brendovi_unutra/FW24/pocetna1.jpg"
    alt="Steve Madden"
    fluid
    className="custom-image mb-3"
  />
</Link>

        </Col>
        <Col md={6} className="custom-col">
          <Row className="top-right-images custom-col">
            <Col md={6} className="custom-col">
            <Link to="/brands/guess" className="image-link">
            <Image
                  src="https://www.fashionandfriends.com/pub/media/wysiwyg/Brendovi_unutra/FW24/pocetna3.jpg"
                  alt="Guess"
                  fluid
                  className="custom-image mb-3"
                />
              </Link>
            </Col>
            <Col md={6} className="custom-col">
            <Link to="/brands/superdry" className="image-link">
            <Image
                  src="https://www.fashionandfriends.com/pub/media/wysiwyg/Brendovi_unutra/FW24/pocetna2.jpg"
                  alt="Superdry"
                  fluid
                  className="custom-image mb-3"
                />
              </Link>
              </Col>
          </Row>
          <Row className="bottom-right-image custom-col">
            <Col className="custom-col">
            <Link to="/brands/tommy-hilfiger" className="image-link">
            <Image
                  src="https://www.fashionandfriends.com/pub/media/wysiwyg/Brendovi_unutra/FW24/pocetna4.jpg"
                  alt="Tommy Hilfiger"
                  fluid
                  className="custom-image"
                />
              </Link>
              </Col>
          </Row>
        </Col>
      </Row>
    </Container>
  );
};

export default MainGrid;
