import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./OneProduct.css";

const OneProduct = () => {
  const { category, subCategory, productId } = useParams(); // Get category, subCategory, and productId from URL params
  const [product, setProduct] = useState(null); // State for product details
  const [suggestions, setSuggestions] = useState([]); // State for product suggestions
  const navigate = useNavigate(); // Hook to navigate programmatically

  useEffect(() => {
    // Fetch product details
    const fetchProduct = async () => {
        try {
          const response = await fetch(`/api/products/${category}/${subCategory}/${productId}`);
          if (!response.ok) {
            throw new Error(`HTTP error! status: ${response.status}`);
          }
          const data = await response.json();
          setProduct(data);
      
          // Fetch random suggestions
          const suggestionsResponse = await fetch(`/api/products/random?limit=3`);
          if (!suggestionsResponse.ok) {
            throw new Error(`HTTP error! status: ${suggestionsResponse.status}`);
          }
          const suggestionsData = await suggestionsResponse.json();
          setSuggestions(suggestionsData);
        } catch (error) {
          console.error('Error fetching product:', error);
          // Optionally, you can set an error state here to display an error message in the UI
        }
      };
      

    fetchProduct();
  }, [category, subCategory, productId]);

  const handleSuggestionClick = (id) => {
    // Navigate to the selected suggestion's product page
    navigate(`/products/${category}/${subCategory}/${id}`);
  };

  if (!product) return <div>Loading...</div>; // Loading state

  return (
    <div className="product-container">
      <div className="product-details">
        <div className="product-image">
          <img src={product.imageUrl} alt={product.name} />
        </div>
        <div className="product-info">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <p className="product-price">${product.price}</p>
          <div className="product-dimensions">
            <p>Dimensions: {product.dimensions}</p>
          </div>
          <button className="contact-advisor-btn">Contact an Advisor</button>
        </div>
      </div>
      <div className="suggestions">
        <h2>Suggestions</h2>
        <div className="suggestions-container">
          {suggestions.map((suggestion) => (
            <div
              key={suggestion.id}
              className="suggestion-item"
              onClick={() => handleSuggestionClick(suggestion.id)} // Handle click
            >
              <img src={suggestion.imageUrl} alt={suggestion.name} />
              <p>{suggestion.name}</p>
              <p className="suggestion-price">${suggestion.price}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
};

export default OneProduct;
