import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Container, Row, Col, Image } from "react-bootstrap";
import "./MainGrid.css";
import axios from "axios";

const MainGrid = () => {
  const [brands, setBrands] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchBrands = async () => {
      try {
        const response = await axios.get("/api/brands"); // Adjust endpoint as necessary
        setBrands(response.data);
      } catch (err) {
        setError("Failed to load brands");
      }
    };

    fetchBrands();
  }, []);

  if (error) {
    return <div>{error}</div>;
  }

  return (
    <Container className="mt-4 image-grid">
      <Row>
        {brands.map((brand) => (
          <Col md={6} key={brand._id} className="custom-col">
            <Link to={`/brands/${brand._id}`} className="image-link">
              <Image
                src={brand.images[0]} // Use the first image from the images array
                alt={brand.name}
                fluid
                className="custom-image mb-3"
              />
              <h5>{brand.name}</h5> {/* Show brand name */}
            </Link>
          </Col>
        ))}
      </Row>
    </Container>
  );
};

export default MainGrid;
