import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./ShoppingCart.css";
import NavigationBar from "../NavigationBar/NavigationBar";
import Footer from "../Footer/Footer";
import { FaTrash, FaPlus, FaMinus } from 'react-icons/fa';

const ShoppingCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetchCartItems();
  }, []);

  const fetchCartItems = async () => {
    try {
      const response = await axios.get("/api/cart");
      setCartItems(response.data.items);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching cart items:", error);
      setError("Failed to fetch cart items");
      setLoading(false);
    }
  };

  const handleQuantityChange = async (productId, newQuantity) => {
    if (newQuantity < 1) return;
    try {
      await axios.put(`/api/cart/item/${productId}`, { quantity: newQuantity });
      fetchCartItems();
    } catch (error) {
      console.error("Error updating quantity:", error);
      setError("Failed to update quantity");
    }
  };

  const handleRemoveItem = async (productId) => {
    try {
      await axios.delete(`/api/cart/item/${productId}`);
      fetchCartItems();
    } catch (error) {
      console.error("Error removing item:", error);
      setError("Failed to remove item");
    }
  };

  const clearCart = async () => {
    try {
      await axios.delete("/api/cart");
      fetchCartItems();
    } catch (error) {
      console.error("Error clearing cart:", error);
      setError("Failed to clear cart");
    }
  };

  const getTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  const handleCheckout = () => {
    if (cartItems.length === 0) {
      alert("Your cart is empty!");
      return;
    }
    navigate('/checkout', { state: { cartItems, totalPrice: getTotalPrice() } });
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <NavigationBar />
      <div className="cart-page">
        <h1 className="cart-title">Your Shopping Cart</h1>
        {cartItems.length === 0 ? (
          <div className="empty-cart">
            <p>Your cart is empty.</p>
            <Link to="/products" className="continue-shopping-btn">
              Continue Shopping
            </Link>
          </div>
        ) : (
          <div className="cart-content">
            <div className="cart-items">
              {cartItems.map((item) => (
                <div key={item.productId} className="cart-item">
                  <img src={item.image} alt={item.name} className="cart-item-image" />
                  <div className="cart-item-details">
                    <h3>{item.name}</h3>
                    <p className="cart-item-price">{item.price} Lek</p>
                  </div>
                  <div className="cart-item-quantity">
                    <button onClick={() => handleQuantityChange(item.productId, item.quantity - 1)}>
                      <FaMinus />
                    </button>
                    <input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) => handleQuantityChange(item.productId, parseInt(e.target.value))}
                    />
                    <button onClick={() => handleQuantityChange(item.productId, item.quantity + 1)}>
                      <FaPlus />
                    </button>
                  </div>
                  <p className="cart-item-subtotal">
                    {(item.price * item.quantity).toFixed(2).toLocaleString()} Lek
                  </p>
                  <button className="remove-item" onClick={() => handleRemoveItem(item.productId)}>
                    <FaTrash />
                  </button>
                </div>
              ))}
            </div>
            <div className="cart-summary">
              <h2>Order Summary</h2>
              <div className="summary-row">
                <span>Subtotal</span>
                <span>{getTotalPrice().toLocaleString()} Lek</span>
              </div>
              <div className="summary-row">
                <span>Shipping</span>
                <span>0.00 Lek</span>
              </div>
              <div className="summary-row total">
                <span>Total</span>
                <span>{getTotalPrice().toLocaleString()} Lek</span>
              </div>
              <button onClick={handleCheckout} className="checkout-btn">
                Proceed to Checkout
              </button>
              <button className="clear-cart-btn" onClick={clearCart}>
                Clear Cart
              </button>
              <Link to="/products" className="continue-shopping-btn">
                Continue Shopping
              </Link>
            </div>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default ShoppingCart;
