import React, { useState } from "react";
import Button from "react-bootstrap/Button";
import Form from "react-bootstrap/Form";
import "./ContactUs.css";
import NavigationBar from "./NavigationBar";
import Footer from "./Footer";
import axios from "axios";

function ContactUs() {
  const [newContact, setNewContact] = useState({
    firstName: "",
    lastName: "",
    email: "",
    message: "",
  });

  const [sms, setSms] = useState("");

  const handleSubmit = (e) => {
    e.preventDefault();
    axios
      .post("http://localhost:5000/create", newContact)
      .then((res) => {
        console.log("Message sent");
        setSms("Message sent! Thank you for contacting us.");
      })
      .catch((err) => {
        console.log("Couldn't send:" + err);
      });
  };
  return (
    <div>
      <NavigationBar />
<div className="contact-form-container">
  <div className="circle-div">
    <div id="circle1"></div>
    <div id="circle2"></div>
    <div id="circle3"></div>
  </div>
  <div className="contact-form-wrapper"> {/* Wrap the form in a separate div */}
    <h2 className="contact-form-title">Send us a message</h2>
    <Form className="contact-form" onSubmit={handleSubmit}>
      <Form.Group className="mb-3" controlId="firstName">
        <Form.Label>First Name</Form.Label>
        <Form.Control
          type="text"
          value={newContact.firstName}
          onChange={(e) =>
            setNewContact({ ...newContact, firstName: e.target.value })
          }
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="lastName">
        <Form.Label>Last Name</Form.Label>
        <Form.Control
          type="text"
          value={newContact.lastName}
          onChange={(e) =>
            setNewContact({ ...newContact, lastName: e.target.value })
          }
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="email">
        <Form.Label>Email address</Form.Label>
        <Form.Control
          type="email"
          value={newContact.email}
          onChange={(e) =>
            setNewContact({ ...newContact, email: e.target.value })
          }
        />
      </Form.Group>

      <Form.Group className="mb-3" controlId="message">
        <Form.Label>Message</Form.Label>
        <Form.Control
          as="textarea"
          rows={4}
          placeholder="Enter your message here..."
          value={newContact.message}
          onChange={(e) =>
            setNewContact({ ...newContact, message: e.target.value })
          }
        />
      </Form.Group>

      <Button variant="dark" type="submit" className="submit-button">
        Submit
      </Button>
    </Form>
  </div>
  <p>{sms}</p>
  <Footer />
</div>
</div>

  );
}

export default ContactUs;
