import React, { useState, useEffect, useRef } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./NavigationBar.css";
import axios from "axios";

const NavigationBar = ({ cartItemsCount }) => {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [navbarOpen, setNavbarOpen] = useState(false);
  const [navClass, setNavClass] = useState("navbar-black");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [isSearchResultsVisible, setIsSearchResultsVisible] = useState(false);
  const [mobileMenuState, setMobileMenuState] = useState("main");
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  const navigate = useNavigate();
  const navbarRef = useRef(null);
  const searchRef = useRef(null);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (navbarRef.current && !navbarRef.current.contains(event.target)) {
        setNavbarOpen(false);
        setNavClass("navbar-black");
        setMobileMenuState("main");
      }
    };

    const handleResize = () => {
      setIsMobile(window.innerWidth <= 768);
      if (window.innerWidth > 768) {
        setNavbarOpen(false);
        setNavClass("navbar-black");
        setMobileMenuState("main");
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    window.addEventListener("resize", handleResize);

    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
      window.removeEventListener("resize", handleResize);
    };
  }, []);

  const handleSearch = async (query) => {
    setSearchQuery(query);

    if (query.length > 2) {
      try {
        const response = await axios.get(
          `http://localhost:5000/api/search?q=${query}`
        );
        setSearchResults(response.data);
        setIsSearchResultsVisible(true);
      } catch (error) {
        console.error("Error fetching search results:", error);
      }
    } else {
      setSearchResults([]);
      setIsSearchResultsVisible(false);
    }
  };

  const toggleSearch = () => setIsSearchOpen(!isSearchOpen);

  const toggleNavbar = () => {
    setNavbarOpen(!navbarOpen);
    setNavClass(navbarOpen ? "navbar-black" : "show");
    setMobileMenuState("main");
  };

  const handleMobileMenuClick = (category) => {
    setMobileMenuState(mobileMenuState === category ? "main" : category);
  };

  const handleItemClick = (path) => {
    navigate(path);
    setNavbarOpen(false);
    setNavClass("navbar-black");
    setMobileMenuState("main");
  };

  const renderMobileMenu = () => {
    switch (mobileMenuState) {
      case "main":
        return (
          <>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("new")}>
              New
            </div>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("brands")}>
              Brands
            </div>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("bags")}>
              Bags
            </div>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("accessories")}>
              Accessories
            </div>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("fragrances")}>
              Fragrances
            </div>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("sale")}>
              Sale
            </div>
            <div className="navbar-item" onClick={() => handleItemClick("/contact")}>
              Contact Us
            </div>
          </>
        );
      case "new":
        return (
          <div className="navbar-item" onClick={() => handleItemClick("/products/new")}>
            New Arrivals
          </div>
        );
      case "brands":
        return (
          <>
            <div className="navbar-item" onClick={() => handleItemClick("/brands/chanel")}>Chanel</div>
            <div className="navbar-item" onClick={() => handleItemClick("/brands/dior")}>Dior</div>
            <div className="navbar-item" onClick={() => handleItemClick("/brands/gucci")}>Gucci</div>
            <div className="navbar-item" onClick={() => handleItemClick("/brands/louis-vuitton")}>Louis Vuitton</div>
            <div className="navbar-item" onClick={() => handleItemClick("/brands/prada")}>Prada</div>
            <div className="navbar-item" onClick={() => handleItemClick("/brands/hermes")}>Hermès</div>
            {/* Add more brands as needed */}
          </>
        );
      case "bags":
        return (
          <>
            <div className="navbar-item" onClick={() => handleItemClick("/products/bags/backpacks")}>Backpacks</div>
            <div className="navbar-item" onClick={() => handleItemClick("/products/bags/crossbody-bags")}>Crossbody Bags</div>
            {/* Add more bag items as needed */}
          </>
        );
      // Add cases for accessories, fragrances, and sale
      default:
        return null;
    }
  };

  return (
    <div className="navbar-container" ref={navbarRef}>
      <div className="navbar-logo">
        {isMobile && (
          <button className="hamburger-menu" onClick={toggleNavbar}>
            <svg xmlns="http://www.w3.org/2000/svg" height="24px" viewBox="0 -960 960 960" width="24px" fill="#5f6368">
              <path d="M120-240v-80h720v80H120Zm0 480v-80h720v80H120Zm0-240v-80h720v80H120Z" />
            </svg>
          </button>
        )}
        <Link to="/">
          <h1 className="logo-nav">DESIGNER.AL</h1>
        </Link>
      </div>
      <div className={`${navClass} ${isMobile ? "mobile" : ""}`}>
        {navbarOpen ? (
          renderMobileMenu()
        ) : (
          <>
            <div className="navbar-item">
              <Link to="/products/new">New</Link>
            </div>
            <div className="navbar-item">
              <Link to="/brands">Brands</Link>
            </div>
            <div className="navbar-item">
              <Link to="/products/bags">Bags</Link>
            </div>
            <div className="navbar-item">
              <Link to="/products/accessories">Accessories</Link>
            </div>
            <div className="navbar-item">
              <Link to="/products/fragrances">Fragrances</Link>
            </div>
            <div className="navbar-item">
              <Link to="/products/sale">Sale</Link>
            </div>
            <div className="navbar-item">
              <Link to="/contact">Contact Us</Link>
            </div>
          </>
        )}

        <div className="icon-container">
          <div className="search-container navbar-item" ref={searchRef}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="24px"
              viewBox="0 -960 960 960"
              width="24px"
              fill="#5f6368"
              onClick={toggleSearch}
            >
              <path d="M784-120 532-372q-30 24-69 38t-83 14q-109 0-184.5-75.5T120-580q0-109 75.5-184.5T380-840q109 0 184.5 75.5T640-580q0 44-14 83t-38 69l252 252-56 56ZM380-400q75 0 127.5-52.5T560-580q0-75-52.5-127.5T380-760q-75 0-127.5 52.5T200-580q0 75 52.5 127.5T380-400Z" />
            </svg>
            {isSearchOpen && (
              <div className="search-input-container">
                <input
                  type="text"
                  placeholder="Search for products..."
                  className="search-input"
                  value={searchQuery}
                  onChange={(e) => handleSearch(e.target.value)}
                />
                {isSearchResultsVisible && (
                  <div className="search-results-popup">
                    {searchResults.length > 0 ? (
                      searchResults.map((product) => {
                        // Filter out "new" and "sale" from category and subCategory
                        const filteredCategory = Array.isArray(product.category)
                          ? product.category.filter(
                              (cat) => cat !== "new" && cat !== "sale"
                            )
                          : [product.category].filter(
                              (cat) => cat !== "new" && cat !== "sale"
                            );

                        const filteredSubCategory = Array.isArray(
                          product.subCategory
                        )
                          ? product.subCategory.filter(
                              (sub) => sub !== "new" && sub !== "sale"
                            )
                          : [product.subCategory].filter(
                              (sub) => sub !== "new" && sub !== "sale"
                            );

                        // Construct the URL path
                        const urlPath = [
                          ...filteredCategory,
                          ...filteredSubCategory,
                          product._id,
                        ].join("/");

                        return (
                          <Link
                            to={`/products/${urlPath}`} // Use the dynamic path here
                            key={product._id}
                            className="search-result-item"
                            onClick={() => setIsSearchResultsVisible(false)}
                          >
                            <img
                              src={product.image}
                              alt={product.name}
                              className="search-result-image"
                            />
                            <div className="search-result-details">
                              <span className="search-result-name">
                                {product.name}
                              </span>
                              <span className="search-result-price">
                                ${product.price.toFixed(2)}
                              </span>
                            </div>
                          </Link>
                        );
                      })
                    ) : (
                      <div className="no-results">No products found</div>
                    )}
                  </div>
                )}
              </div>
            )}
          </div>

          <Link to="/cart" className="cart-icon navbar-item">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="24px"
              viewBox="0 -960 960 960"
              width="24px"
              fill="#5f6368"
            >
              <path d="M240-80q-33 0-56.5-23.5T160-160v-480q0-33 23.5-56.5T240-720h80q0-66 47-113t113-47q66 0 113 47t47 113h80q33 0 56.5 23.5T800-640v480q0 33-23.5 56.5T720-80H240Zm0-80h480v-480h-80v80q0 17-11.5 28.5T600-520q-17 0-28.5-11.5T560-560v-80H400v80q0 17-11.5 28.5T360-520q-17 0-28.5-11.5T320-560v-80h-80v480Zm160-560h160q0-33-23.5-56.5T480-800q-33 0-56.5 23.5T400-720q0 44-14 83t-38 69l252 252-56 56ZM240-160v-480 480Z" />
            </svg>
            <span className="cart-count">{cartItemsCount}</span>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default NavigationBar;
