import React, { useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import ShoppingCartOutlinedIcon from "@mui/icons-material/ShoppingCartOutlined";
import "./NavigationBar.css";
import { Container, Nav, Navbar, Form, NavDropdown } from "react-bootstrap";

const NavigationBar = () => {
  const [searchBar, setSearchBar] = useState(false);
  const [brand, setBrand] = useState(true);
  const [isNavbarOpen, setIsNavbarOpen] = useState(false);

  const toggleSearch = () => setSearchBar(!searchBar);
  const toggleBrand = () => setBrand(!brand);
  const toggleNavbar = () => setIsNavbarOpen(!isNavbarOpen);

  return (
    <Navbar expand="lg" className="navbar">
      <Container fluid>
        <Navbar.Toggle aria-controls="navbarScroll" onClick={toggleNavbar} />
        <Navbar.Collapse id="navbarScroll" className={isNavbarOpen ? 'show' : ''}>
          <span className="close-btn" onClick={toggleNavbar}>Ã—</span>
          <Nav className="me-auto my-2 my-lg-0" style={{ maxHeight: "100px" }} navbarScroll>
            <Nav.Link href="/about">About Us</Nav.Link>
            <Nav.Link href="/contact">Contact Us</Nav.Link>
            <NavDropdown title="Explore" id="navbarScrollingDropdown">
              <NavDropdown.Item href="/products">Fragrances</NavDropdown.Item>
              <NavDropdown.Item href="/products">Accessories</NavDropdown.Item>
              <NavDropdown.Item href="#action5">Hair Care</NavDropdown.Item>
              <NavDropdown.Item href="#action6">Makeup</NavDropdown.Item>
              <NavDropdown.Item href="#action7">Sets</NavDropdown.Item>
            </NavDropdown>
          </Nav>
          {brand && (
            <Navbar.Brand href="#" className="brand">
              DESIGNER.AL
            </Navbar.Brand>
          )}
          <Form className="d-flex">
            {searchBar && (
              <Form.Control
                type="search"
                placeholder="Search"
                className="me-2"
                aria-label="Search"
              />
            )}
            <SearchIcon onClick={toggleSearch} className="icon" />
            <div className="cart-icon">
              <ShoppingCartOutlinedIcon className="icon" />
              <span>0</span>
            </div>
          </Form>
        </Navbar.Collapse>
      </Container>
    </Navbar>
  );
};

export default NavigationBar;
