import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './NewProduct.css';
import NavigationBar from '../NavigationBar/NavigationBar';
import Footer from '../Footer/Footer';

const NewProduct = () => {
    const [product, setProduct] = useState({
        _id: '',
        name: '',
        price: '',
        image: '',
        category: [],
        subCategory: '',
        description: '',
        quantityStock: '',
        brand: '',
    });
    const [idAlert, setIdAlert] = useState('');

    const handleChange = (e) => {
        const { name, value } = e.target;
        setProduct({ ...product, [name]: value });
        if (name === '_id') {
            setIdAlert('');
        }
    };

    const checkProductId = async (inputId) => {
        try {
            const response = await fetch(`/check-id/${inputId}`);
            const data = await response.json();
            if (data.exists) {
                console.log('Product found:', data.product);
                // Handle product found (e.g., display product details)
            } else {
                console.log('Product not found');
                // Handle product not found (e.g., display error message)
            }
        } catch (error) {
            console.error('Error checking product ID:', error);
            // Handle error (e.g., display error message)
        }
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        if (idAlert) {
            alert('Please resolve the ID issue before submitting.');
            return;
        }
        try {
            const response = await axios.post('http://localhost:5000/api/products/add', product);
            console.log('Product added:', response.data);
            alert('Product added successfully!');
            setProduct({
                _id: '',
                name: '',
                price: '',
                image: '',
                category: [],
                subCategory: '',
                description: '',
                quantityStock: '',
                brand: '',
            });
            setIdAlert('');
        } catch (error) {
            console.error('Error adding product:', error.response?.data || error.message);
            alert('Error adding product. Please try again.');
        }
    };

    useEffect(() => {
        console.log('ID Alert updated:', idAlert);
    }, [idAlert]);

    console.log('Current ID Alert:', idAlert);

    return (
        <div className="add-product-page">
            <NavigationBar />
            <div className="container">
                <h2>New Product</h2>
                <form onSubmit={handleSubmit} className="add-product-form">
                    <div className="form-group">
                        <input
                            type="text"
                            name="_id"
                            value={product._id}
                            onChange={handleChange}
                            onBlur={checkProductId}
                            placeholder="Product ID"
                            required
                        />
                        {idAlert && (
                            <p className="id-alert" style={{
                                color: 'red',
                                fontWeight: 'bold',
                                backgroundColor: 'lightyellow',
                                padding: '10px',
                                border: '1px solid red',
                                borderRadius: '4px',
                                marginTop: '5px'
                            }}>
                                {idAlert}
                            </p>
                        )}
                    </div>
                    <input
                        type="text"
                        name="name"
                        value={product.name}
                        onChange={handleChange}
                        placeholder="Product Name"
                        required
                    />
                    <input
                        type="number"
                        name="price"
                        value={product.price}
                        onChange={handleChange}
                        placeholder="Price"
                        required
                    />
                    <input
                        type="text"
                        name="image"
                        value={product.image}
                        onChange={handleChange}
                        placeholder="Image URL"
                        required
                    />
                    <input
                        type="text"
                        name="category"
                        value={product.category}
                        onChange={handleChange}
                        placeholder="Categories (comma-separated)"
                        required
                    />
                    <input
                        type="text"
                        name="subCategory"
                        value={product.subCategory}
                        onChange={handleChange}
                        placeholder="Sub-category"
                        required
                    />
                    <textarea
                        name="description"
                        value={product.description}
                        onChange={handleChange}
                        placeholder="Description"
                        required
                    ></textarea>
                    <input
                        type="number"
                        name="quantityStock"
                        value={product.quantityStock}
                        onChange={handleChange}
                        placeholder="Quantity in Stock"
                        required
                    />
                    <input
                        type="text"
                        name="brand"
                        value={product.brand}
                        onChange={handleChange}
                        placeholder="Brand"
                        required
                    />
                    <button type="submit">Add Product</button>
                </form>
            </div>
            <Footer />
        </div>
    );
};

export default NewProduct;
