import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "./OneProduct.css";
import Footer from "../Footer/Footer";
import NavigationBar from "../NavigationBar/NavigationBar";
import Slider from "react-slick";
import uuidv4 from 'uuid'; // Import uuidv4 for generating user ID

const OneProduct = () => {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [suggestions, setSuggestions] = useState([]); // State for suggested products
  const { category, subCategory, id } = useParams();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const baseUrl =
          process.env.REACT_APP_API_URL || "http://localhost:5000";
        const url = `${baseUrl}/api/products/${category}/${subCategory}/${id}`;
        const response = await axios.get(url);
        setProduct(response.data);
        setLoading(false);
      } catch (err) {
        setError(`Failed to fetch product: ${err.message}`);
        setLoading(false);
      }
    };

    const fetchSuggestions = async () => {
      try {
        const baseUrl =
          process.env.REACT_APP_API_URL || "http://localhost:5000";
        console.log(
          "Fetching suggestions from:",
          `${baseUrl}/api/products/suggestions`
        );
        const response = await axios.get(`${baseUrl}/api/products/suggestions`);
        console.log("Suggestions response:", response);
        setSuggestions(response.data);
        console.log("Fetched suggestions:", response.data);
      } catch (err) {
        console.error("Failed to fetch suggestions:", err);
        console.error(
          "Error details:",
          err.response ? err.response.data : "No response data"
        );
      }
    };

    fetchProduct();
    fetchSuggestions();
  }, [category, subCategory, id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;
  if (!product) return <div>Product not found</div>;
  const addToCart = async () => {
    try {
      const userId = getUserId(); // Implement this function similar to other components
      const response = await axios.post(`http://localhost:5000/api/cart/${userId}/add`, {
        productId: product._id,
        quantity: quantity,
      });
      console.log("Product added to cart:", response.data);
      // You might want to show a success message or update the UI here
    } catch (error) {
      console.error("Error adding product to cart:", error);
      // Handle the error, maybe show an error message to the user
    }
  };

  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3, // Show 3 products at a time
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  // Render product details here
  return (
    <div>
      <NavigationBar />
      <div className="product-container">
        <div className="product-image">
          <img src={product.image} alt={product.name} />
        </div>
        <div className="product-info">
          <h1>{product.name}</h1>
          <p className="product-description">{product.description}</p>
          <p className="product-price">${product.price.toFixed(2)}</p>
          <div className="quantity-selector">
            <button onClick={() => setQuantity(Math.max(1, quantity - 1))}>
              -
            </button>
            <span>{quantity}</span>
            <button onClick={() => setQuantity(quantity + 1)}>+</button>
          </div>
          <button className="add-to-cart-btn" onClick={addToCart}>
            ADD TO CART
          </button>
        </div>
      </div>
      <div className="suggestions">
        <h2>Suggestions</h2>
        {suggestions.length > 0 ? (
          <Slider {...carouselSettings}>
            {suggestions.map((suggestion) => (
              <div key={suggestion._id} className="suggestion-item">
                <Link
                  to={`/products/${suggestion.category}/${suggestion.subCategory}/${suggestion._id}`}
                >
                  <img
                    src={suggestion.image}
                    alt={suggestion.name}
                    className="suggestion-image"
                  />
                  <p>{suggestion.name}</p>
                  <p>${suggestion.price.toFixed(2)}</p>
                </Link>
              </div>
            ))}
          </Slider>
        ) : (
          <p>No suggestions available</p>
        )}
      </div>
      <Footer />
    </div>
  );
};

// Add the getUserId function if not already present
const getUserId = () => {
  let userId = localStorage.getItem("userId");
  if (!userId) {
    userId = uuidv4(); // Make sure to import uuidv4 from 'uuid'
    localStorage.setItem("userId", userId);
  }
  return userId;
};

export default OneProduct;
