import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import "./OneProduct.css";

const OneProduct = () => {
  const { category, subCategory, productId } = useParams(); // Get category, subCategory, and productId from URL params
  const [product, setProduct] = useState(null); // State for product details
  const [suggestions, setSuggestions] = useState([]); // State for product suggestions
  const navigate = useNavigate(); // Hook to navigate programmatically
  const [error, setError] = useState(null); // Add this to your state variables

  useEffect(() => {
    // Fetch product details
    const fetchProduct = async () => {
      try {
        // Fetching the product
        const response = await fetch(
          `/api/products/${category}/${subCategory}/${productId}`
        );

        // Check if the response is ok (status in the range 200-299)
        if (!response.ok) {
          const errorText = await response.text(); // Get the response text for detailed info
          throw new Error(
            `HTTP error! status: ${response.status}. Response: ${errorText}`
          );
        }

        const data = await response.json();
        setProduct(data);

        // Fetching random suggestions
        const suggestionsResponse = await fetch(`/api/products/random?limit=3`);

        if (!suggestionsResponse.ok) {
          const suggestionsErrorText = await suggestionsResponse.text(); // Get the response text
          throw new Error(
            `HTTP error when fetching suggestions! status: ${suggestionsResponse.status}. Response: ${suggestionsErrorText}`
          );
        }

        const suggestionsData = await suggestionsResponse.json();
        setSuggestions(suggestionsData);
      } catch (error) {
        console.error("Error fetching product:", error.message);
        setError(error.message); // Set error state
      }
    };

    fetchProduct();
  }, [category, subCategory, productId]);

  const handleSuggestionClick = (id) => {
    // Navigate to the selected suggestion's product page
    navigate(`/products/${category}/${subCategory}/${id}`);
  };

  // Display error message if it exists
  if (error) return <div className="error-message">Error: {error}</div>; // Display error if fetching fails
  if (!product) return <div>Loading...</div>; // Loading state

  return (
    <div className="product-container">
      <div className="product-image">
        <img src={product.image} alt={product.name} />
      </div>
      <div className="product-info">
        <h1>{product.name}</h1>
        <p className="product-description">{product.description}</p>
        <p className="product-ref">Ref. {product.ref}</p>
        <p className="product-price">${product.price.toFixed(2)}*</p>
        <p className="product-dimensions">
          {product.dimensions.width} x {product.dimensions.height} x {product.dimensions.depth} in ( cm )
        </p>
        <div className="variants">
          <h3>VARIANTS ({product.variants.length})</h3>
          <div className="variant-options">
            {product.variants.map((variant, index) => (
              <div key={index} className="variant-option">
                <img src={variant.image} alt={`Variant ${index + 1}`} />
              </div>
            ))}
          </div>
        </div>
        <button className="contact-advisor-btn">CONTACT AN ADVISOR</button>
        <div className="discover-more">
          <a href="#">DISCOVER MORE CREATIONS IN BOUTIQUES</a>
          <div className="info-icon">?</div>
        </div>
        <p className="suggested-price">
          *Suggested retail price. <a href="#">More information</a>
        </p>
      </div>
    </div>
  );
};

export default OneProduct;
