import React, { useState } from 'react'
import { Container, Form, Button, Alert } from 'react-bootstrap'
import axios from 'axios'
import { useNavigate } from 'react-router-dom'
import Footer from '../Footer/Footer';
import NavigationBar from '../NavigationBar/NavigationBar';
import './LogIn.css'

const LogIn = () => {
    const nav = useNavigate()
    const [userLog, setUserLog] = useState({
        email: "",
        password: ""
    })

    const [error, setError] = useState("")
    const [alert, setAlert] = useState("")

    const handleChange = (e) => {
        setUserLog({ ...userLog, [e.target.name]: e.target.value })
    }

    const handleSubmit = async (e) => {
        e.preventDefault();
        setError("");
        setAlert("");

        if (!userLog.email || !userLog.password) {
            setError('Please fill in all fields');
            setAlert("danger");
            return;
        }

        try {
            const res = await axios.post('http://localhost:5000/api/user/login/', userLog, {
                withCredentials: true
            });
            
            // Save user info to localStorage
            localStorage.setItem('userInfo', JSON.stringify(res.data));
            
            // Optionally, you can log to confirm the data is saved
            console.log('User info saved:', res.data);
            
            // Navigate to home page
            nav('/');
        } catch (err) {
            console.error("Login error:", err);
            setAlert('danger');
            setError(err.response?.data?.message || 'Login failed. Please try again.');
        }
    };

    return (
        <div className="login-page">
            <NavigationBar />
            <Container className="login-container">
                <Form className="login-form" onSubmit={handleSubmit}>
                    <Form.Group className="login-form-group">
                        <Form.Label className="login-form-label">Email</Form.Label>
                        <Form.Control
                            className="login-form-control"
                            type="email"
                            onChange={handleChange}
                            name="email"
                            value={userLog.email}
                            placeholder="Enter email"
                        />
                    </Form.Group>
                    <Form.Group className="login-form-group">
                        <Form.Label className="login-form-label">Password</Form.Label>
                        <Form.Control
                            className="login-form-control"
                            type="password"
                            name="password"
                            onChange={handleChange}
                            value={userLog.password}
                            placeholder="Enter password"
                        />
                    </Form.Group>
                    <Button 
                        className="login-submit-btn" 
                        variant="primary" 
                        type="submit"
                        onClick={handleSubmit}
                    >
                        Login
                    </Button>
                </Form>
                <p className='login-register-link'>
                    Don't have an account? <a href="/register">Register</a>
                </p>
                <Alert variant={alert} className='login-alert'>
                    {error}
                </Alert>
            </Container>
            <Footer />
        </div>
    )
}

export default LogIn
