import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import "./OneProduct.css";

const OneProduct = () => {
  const { category, subCategory, id } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await fetch(
          `/api/products/${category}/${subCategory}/${id}`
        );

        if (!response.ok) {
          // Check for non-OK response and throw error with status
          throw new Error(
            `Server error: ${response.status} ${response.statusText}`
          );
        }

        const contentType = response.headers.get("content-type");

        // Check if the response is actually JSON
        if (!contentType || !contentType.includes("application/json")) {
          const rawText = await response.text(); // Capture raw text response
          console.error("Unexpected response format (not JSON):", rawText);
          throw new Error("Expected JSON, but received non-JSON response");
        }

        // Process the JSON data
        const data = await response.json();
        setProduct(data); // Set the product in state
      } catch (error) {
        console.error("Error fetching product:", error); // Log any errors
      } finally {
        setLoading(false); // Ensure loading is stopped regardless of the outcome
      }
    };

    fetchProduct();
  }, [category, subCategory, id]);

  if (loading) return <div>Loading...</div>;
  if (!product) return <div>Product not found</div>;

  return (
    <div className="product-container">
      <div className="product-details">
        <div className="product-image">
          <img src={product.image} alt={product.name} />
        </div>
        <div className="product-info">
          <h1>{product.name}</h1>
          <p>{product.description}</p>
          <p className="product-price">${product.price}</p>
          <div className="product-dimensions">
            <p>Brand: {product.brand}</p>
            <p>Stock: {product.quantityStock}</p>
          </div>
          <button className="contact-advisor-btn">Contact an Advisor</button>
        </div>
      </div>

      {product.suggestions && product.suggestions.length > 0 && (
        <div className="suggestions">
          <h2>Suggestions</h2>
          <div className="suggestions-container">
            {product.suggestions.map((suggestion) => (
              <div key={suggestion._id} className="suggestion-item">
                <img src={suggestion.image} alt={suggestion.name} />
                <p>{suggestion.name}</p>
                <p className="suggestion-price">${suggestion.price}</p>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
};

export default OneProduct;
