import React, { useEffect } from "react"; // Import React and useEffect//+
import "./App.css";
import ContactUs from "./components/ContactUs/ContactUs";
import AboutUs from "./components/AboutUs/AboutUs";
import Home from "./components/Home";
import Products from "./components/Products/Products";
import ShoppingCart from "./components/ShoppingCart/ShoppingCart";
import { Routes, Route } from "react-router-dom";
import Checkout from "./components/Checkout/Checkout";
import Brand from "./components/Brand/Brand";
import OneProduct from "./components/OneProduct/OneProduct";
import NewProduct from "./components/NewProduct/NewProduct";

function App() {
  const getIpAddress = async () => {
    // Assume you get the IP address from some external API
    const response = await fetch("https://api.ipify.org?format=json");
    const data = await response.json();
    return data.ip; // This is your userId now
  };
  const saveUserIdToLocalStorage = async () => {
    const userId = await getIpAddress();
    localStorage.setItem("userId", userId);
  };
  useEffect(() => {//-
    saveUserIdToLocalStorage();//-
  }, []);

  return (
    <div className="app">
      <Routes>
      <Route path="*" element={<div>Error 404: Not found</div>} />
        <Route path="/" element={<Home />} />
        <Route path="/about" element={<AboutUs />} />
        <Route path="/contact" element={<ContactUs />} />
        <Route path="/cart" element={<ShoppingCart />} />
        <Route path="/checkout" element={<Checkout />} />
        <Route path="/brands/:brandId" element={<Brand />} />
        <Route path="/products" element={<Products />} />
        <Route path="/products/:category" element={<Products />} />
        <Route path="/products/:category/:subCategory" element={<Products />} />
        <Route path="/products/:category/:subCategory/:id" element={<OneProduct />} />
        <Route path="/products/add" element={<NewProduct />} />
        </Routes>
    </div>
  );
}

export default App;
