import React, { useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import ShoppingCartOutlinedIcon from "@mui/icons-material/ShoppingCartOutlined";
import { Link } from "react-router-dom";
import "./NavigationBar.css";

const NavigationBar = () => {
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [products, setProducts] = useState([]);
  const [cartCount, setCartCount] = useState(0);

  const toggleSearch = () => setIsSearchOpen(!isSearchOpen);

  const handleSearch = async (e) => {
    e.preventDefault();
    const response = await fetch(`/api/products?search=${searchQuery}`);
    const data = await response.json();
    setProducts(data);
  };

  return (
    <>
      <nav className="navbar">

        <ul className="nav-links">
          <li>
            <Link to="/about">About Us</Link>
          </li>
          <li>
            <Link to="/contact">Contact Us</Link>
          </li>
          <li className="dropdown">
            <span>Explore</span>
            <ul className="dropdown-menu">
              <li>
                <Link to="/products">Fragrances</Link>
              </li>
              <li>
                <Link to="/products">Accessories</Link>
              </li>
              <li>
                <Link to="/products">Hair Care</Link>
              </li>
              <li>
                <Link to="/products">Makeup</Link>
              </li>
              <li>
                <Link to="/products">Sets</Link>
              </li>
            </ul>
          </li>
        </ul>

        <div className="navbar-brand">
          <Link to="/">DESIGNER.AL</Link>
        </div>
        
        <div className="search-cart-container">
          <div className="search-container">
            <SearchIcon className="icon" onClick={toggleSearch} />
            {isSearchOpen && (
              <input
                type="text"
                placeholder="Search for products..."
                className="search-input"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
              />
            )}
          </div>

          <Link to="/cart" className="cart-icon">
            <ShoppingCartOutlinedIcon className="icon" />
            <span className="cart-count">{cartCount}</span>
          </Link>
        </div>
      </nav>
    </>
  );
};

export default NavigationBar;
