import React, { useState } from 'react';
import 'NavigationBar.css';

const Navbar = () => {
  const [hoveredItem, setHoveredItem] = useState(null);

  const handleMouseEnter = (item) => {
    setHoveredItem(item);
  };

  const handleMouseLeave = () => {
    setHoveredItem(null);
  };

  return (
    <div className="navbar-container">
      {/* Black bar */}
      <div className="navbar-black">
        <div className="navbar-item" onMouseEnter={() => handleMouseEnter('new')} onMouseLeave={handleMouseLeave}>
          TË REJA
        </div>
        <div className="navbar-item" onMouseEnter={() => handleMouseEnter('clothing')} onMouseLeave={handleMouseLeave}>
          VESHJE
        </div>
        <div className="navbar-item" onMouseEnter={() => handleMouseEnter('shoes')} onMouseLeave={handleMouseLeave}>
          KËPUCË
        </div>
        <div className="navbar-item" onMouseEnter={() => handleMouseEnter('brands')} onMouseLeave={handleMouseLeave}>
          MARKA
        </div>
        <div className="navbar-item" onMouseEnter={() => handleMouseEnter('bags')} onMouseLeave={handleMouseLeave}>
          ÇANTA
        </div>
        <div className="navbar-item" onMouseEnter={() => handleMouseEnter('accessories')} onMouseLeave={handleMouseLeave}>
          AKSESORË
        </div>
        <div className="navbar-item" onMouseEnter={() => handleMouseEnter('sale')} onMouseLeave={handleMouseLeave}>
          SALE
        </div>
        <div className="navbar-item" onMouseEnter={() => handleMouseEnter('outlet')} onMouseLeave={handleMouseLeave}>
          OUTLET
        </div>
      </div>

      {/* White dropdown */}
      <div className={`dropdown-container ${hoveredItem ? 'active' : ''}`}>
        {hoveredItem === 'accessories' && (
          <div className="dropdown-content">
            <div className="dropdown-column">
              <a href="#">Rripa</a>
              <a href="#">Kapelet</a>
              <a href="#">Çantë Kozmetike</a>
              <a href="#">Çorape</a>
              <a href="#">Të Tjera</a>
            </div>
            <div className="dropdown-column">
              <a href="#">Kapucë Dhe Dorashka</a>
              <a href="#">Syze Dielli</a>
              <a href="#">Portofol</a>
              <a href="#">Shaje</a>
            </div>
            <div className="dropdown-image">
              <img src="path_to_image" alt="Scotch & Soda" />
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Navbar;
