import React, { useState, useEffect } from "react";
import axios from "axios";
import "./Products.css";
import NavigationBar from '../NavigationBar/NavigationBar'
import Footer from "../Footer/Footer";

const ProductsPage = () => {
  const [products, setProducts] = useState([]);
  const [priceRange, setPriceRange] = useState(500);

  // Fetch products from the backend
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/products");
        setProducts(response.data);
      } catch (error) {
        console.error("Error fetching products", error);
      }
    };

    fetchProducts();
  }, []);

  return (
    <div>
      <NavigationBar />
    <div className="container">
      <aside className="filters-section">
        <h2>Filters</h2>

        <div className="filter-group">
          <h3>Categories</h3>
          <label><input type="checkbox" value="clothes" /> Clothes</label>
          <label><input type="checkbox" value="bags" /> Bags</label>
          <label><input type="checkbox" value="fragrances" /> Fragrances</label>
        </div>

        <div className="filter-group">
          <h3>Price Range</h3>
          <input
            type="range"
            min="0"
            max="1000"
            value={priceRange}
            onChange={(e) => setPriceRange(e.target.value)}
          />
          <span>${priceRange}</span>
        </div>

        <div className="filter-group">
          <h3>Brand</h3>
          <select>
            <option value="all">All Brands</option>
            <option value="brand1">Brand 1</option>
            <option value="brand2">Brand 2</option>
            <option value="brand3">Brand 3</option>
          </select>
        </div>

        <div className="filter-group">
          <h3>Color</h3>
          <div className="color-options">
            <span className="color-option" style={{ backgroundColor: "#000" }}></span>
            <span className="color-option" style={{ backgroundColor: "#ff0000" }}></span>
            <span className="color-option" style={{ backgroundColor: "#00ff00" }}></span>
            <span className="color-option" style={{ backgroundColor: "#0000ff" }}></span>
          </div>
        </div>

        <button className="apply-filters">Apply Filters</button>
      </aside>

      <section className="product-grid">
        <h2>All Products</h2>
        <div className="products-list">
          {products.map((product) => (
            <div className="product-card" key={product._id}>
              <img src={product.img} alt={product.name} />
              <div className="product-info">
                <h3>{product.name}</h3>
                <p>${product.price}</p>
                <button className="buy-now">Buy Now</button>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
          <Footer />

    </div>
  );
};

export default ProductsPage;
