import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import "./NavigationBar.css";

const NavigationBar = ({ cartItemsCount }) => {
  const [hoveredItem, setHoveredItem] = useState(null);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [navbarOpen, setNavbarOpen] = useState(false);
  const [navClass, setNavClass] = useState("navbar-black");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [isDropdownVisible, setIsDropdownVisible] = useState(false);
  const [cartCount, setCartCount] = useState(0);

  useEffect(() => {
    // Initialize the cart count to 0 if it's not passed
    if (cartItemsCount) {
      setCartCount(cartItemsCount);
    }
  }, [cartItemsCount]);

  const handleSearch = async (query) => {
    setSearchQuery(query);
    if (query.length > 2) {
      try {
        const response = await fetch(`/api/products/search?q=${query}`);
        const data = await response.json();
        setSearchResults(data);
        setIsDropdownVisible(true);
      } catch (error) {
        console.error("Error fetching search results:", error);
      }
    } else {
      setSearchResults([]);
      setIsDropdownVisible(false);
    }
  };

  const searchRef = useRef(null);

  const toggleSearch = () => setIsSearchOpen(!isSearchOpen);

  const toggleNavbar = () => {
    if (navbarOpen) {
      setNavbarOpen(false);
      setNavClass("hidden");
    } else {
      setNavbarOpen(true);
      setNavClass("show");
    }
  };

  const handleMouseEnter = (item) => {
    setHoveredItem(item);
  };

  const handleMouseLeave = (item) => {
    if (hoveredItem === item) {
      setHoveredItem(null);
    }
  };

  return (
    <div className="navbar-container">
      <div className="navbar-logo">
        <Link className="hamburger-menu" onClick={toggleNavbar}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            height="24px"
            viewBox="0 -960 960 960"
            width="24px"
            fill="#5f6368"
          >
            <path d="M120-680v-80h720v80H120Zm0 480v-80h720v80H120Zm0-240v-80h720v80H120Z" />
          </svg>
        </Link>
        <Link to="/">
          <h1 className="logo-nav">DESIGNER.AL</h1>
        </Link>
      </div>
      <div className={navClass}>
        {/* Your other navbar items */}
        
        <div className="icon-container">
          <div className="search-container navbar-item" ref={searchRef}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="24px"
              viewBox="0 -960 960 960"
              width="24px"
              fill="#5f6368"
              onClick={toggleSearch}
            >
              <path d="M784-120 532-372q-30 24-69 38t-83 14q-109 0-184.5-75.5T120-580q0-109 75.5-184.5T380-840q109 0 184.5 75.5T640-580q0 44-14 83t-38 69l252 252-56 56ZM380-400q75 0 127.5-52.5T560-580q0-75-52.5-127.5T380-760q-75 0-127.5 52.5T200-580q0 75 52.5 127.5T380-400Z" />
            </svg>
            {isSearchOpen && (
              <div className="search-container" ref={searchRef}>
                <input
                  type="text"
                  placeholder="Search for products..."
                  className="search-input"
                  value={searchQuery}
                  onChange={(e) => handleSearch(e.target.value)}
                />
                {isDropdownVisible && (
                  <div className="search-dropdown">
                    {searchResults.length > 0 ? (
                      searchResults.map((product) => (
                        <Link
                          to={`/product/${product._id}`}
                          key={product._id}
                          className="search-result-item"
                        >
                          <img
                            src={product.image}
                            alt={product.name}
                            className="search-result-image"
                          />
                          <span>{product.name}</span>
                        </Link>
                      ))
                    ) : (
                      <div className="no-results">No products found</div>
                    )}
                  </div>
                )}
              </div>
            )}
          </div>

          <Link to="/cart" className="cart-icon navbar-item">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="24px"
              viewBox="0 -960 960 960"
              width="24px"
              fill="#5f6368"
            >
              <path d="M240-80q-33 0-56.5-23.5T160-160v-480q0-33 23.5-56.5T240-720h80q0-66 47-113t113-47q66 0 113 47t47 113h80q33 0 56.5 23.5T800-640v480q0 33-23.5 56.5T720-80H240Zm0-80h480v-480h-80v80q0 17-11.5 28.5T600-520q-17 0-28.5-11.5T560-560v-80H400v80q0 17-11.5 28.5T360-520q-17 0-28.5-11.5T320-560v-80h-80v480Zm160-560h160q0-33-23.5-56.5T480-800q-33 0-56.5 23.5T400-720ZM240-160v-480 480Z" />
            </svg>
            <span className="cart-count">{cartCount}</span>
          </Link>
        </div>
      </div>
    </div>
  );
};

export default NavigationBar;
