import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./ShoppingCart.css";
import NavigationBar from "../NavigationBar/NavigationBar";
import Footer from "../Footer/Footer";

const getCart = () => {
  let cart = {};
  for (let i = 0; i < i < cart.length; i++) {
    cart[i] = 0;
  }
  return cart;
};
const ShoppingCart = (props) => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const getUserId = () => {
    return localStorage.getItem("userId");
  };

  useEffect(() => {
    fetchCartItems();
  }, []);

  const fetchCartItems = async () => {
    try {
      const userId = getUserId();
      if (!userId) {
        console.error("User ID not found");
        return;
      }
      const response = await axios.get(`http://localhost:5000/api/cart/${userId}`);
      setCartItems(response.data.items || []);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching cart items:", error);
      setError("Failed to fetch cart items");
      setLoading(false);
    }
  };

  const handleRemoveItem = async (productId) => {
    try {
      const userId = getUserId();
      await axios.delete(`http://localhost:5000/api/cart/${userId}/item/${productId}`);
      fetchCartItems();
    } catch (err) {
      setError("Failed to remove item");
    }
  };

  const handleUpdateQuantity = async (productId, quantity) => {
    try {
      const userId = getUserId();
      await axios.put(`http://localhost:5000/api/cart/${userId}/item/${productId}`, { quantity });
      fetchCartItems();
    } catch (err) {
      setError("Failed to update quantity");
    }
  };

  const clearCart = async () => {
    try {
      const userId = getUserId();
      await axios.delete(`http://localhost:5000/api/cart/${userId}`);
      fetchCartItems();
    } catch (err) {
      setError("Failed to clear cart");
    }
  };

  const getTotalPrice = () => {
    return cartItems.reduce(
      (total, item) => total + item.price * item.quantity,
      0
    );
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <NavigationBar />
      <div className="cart-page">
        <div className="cart-items-container">
          {cartItems.length === 0 ? (
            <div className="empty-cart">Your cart is empty.</div>
          ) : (
            <table className="cart-table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Price</th>
                  <th>Quantity</th>
                  <th>Subtotal</th>
                </tr>
              </thead>
              <tbody>
                {cartItems.map((item) => (
                  <tr key={item._id} className="cart-item-row">
                    <td className="cart-item-info">
                      <img
                        src={item.image}
                        alt={item.name}
                        className="cart-item-image"
                      />
                      <div className="cart-item-details">
                        <h3>{item.name}</h3>
                        <p>Size: UNI</p>
                        <p>Color: {item.color}</p>
                      </div>
                    </td>
                    <td className="cart-item-price">{item.price} Lek</td>
                    <td className="cart-item-quantity">
                      <input
                        type="number"
                        value={item.quantity}
                        min="1"
                        onChange={(e) =>
                          handleUpdateQuantity(item._id, e.target.value)
                        }
                      />
                    </td>
                    <td className="cart-item-subtotal">
                      {(item.price * item.quantity).toLocaleString()} Lek
                    </td>
                    <td className="cart-item-actions">
                      <button onClick={() => handleRemoveItem(item._id)}>
                        âœ–
                      </button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
          <div className="cart-summary-container">
            <div className="cart-summary">
              <h3>Subtotal: {getTotalPrice().toLocaleString()} Lek</h3>
              <p>Shipping: 0.00 Lek</p>
              <h3>
                Total Order Amount: {getTotalPrice().toLocaleString()} Lek
              </h3>
              <Link to="/checkout" className="checkout-btn">
                PROCEED TO CHECKOUT
              </Link>
            </div>
            <div className="cart-actions">
              <button className="cart-clear-btn" onClick={clearCart}>
                Clear Shopping Cart
              </button>
              <Link to="/products" className="continue-shopping-btn">
                Continue Shopping
              </Link>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ShoppingCart;
