import React, { useState, useEffect, useRef } from "react";
import SearchIcon from "@mui/icons-material/Search";
import ShoppingCartOutlinedIcon from "@mui/icons-material/ShoppingCartOutlined";
import { Link } from "react-router-dom";
import "./NavigationBar.css";

const NavigationBar = () => {
  const [hoveredItem, setHoveredItem] = useState(null);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [products, setProducts] = useState([]);
  const [cartCount, setCartCount] = useState(0);
  const [navbarOpen, setNavbarOpen] = useState(false);
  const [navClass, setNavClass] = useState("navbar-black");

  const searchRef = useRef(null);

  const toggleSearch = () => setIsSearchOpen(!isSearchOpen);

  const toggleNavbar = () => {
    if (navbarOpen) {
      setNavbarOpen(false);
      setNavClass("hidden");
    } else {
      setNavbarOpen(true);
      setNavClass("show");
    }
  };

  const handleSearch = async (e) => {
    e.preventDefault();
    if (searchQuery.trim() === "") return; // Prevent empty queries
    
    try {
      const response = await fetch(`/api/products?search=${searchQuery}`);
      if (!response.ok) {
        throw new Error("Failed to fetch products");
      }
      const data = await response.json();
      setProducts(data); // Update your products state with the search result
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  const handleMouseEnter = (item) => {
    setHoveredItem(item);
  };

  const handleMouseLeave = (item) => {
    if (hoveredItem === item) {
      setHoveredItem(null);
    }
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setIsSearchOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [searchRef]);

  return (
    <div className="navbar-container">
      <div className="navbar-logo">
        <Link className="hamburger-menu" onClick={toggleNavbar}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            height="24px"
            viewBox="0 -960 960 960"
            width="24px"
            fill="#5f6368"
          >
            <path d="M120-680v-80h720v80H120Zm0 480v-80h720v80H120Zm0-240v-80h720v80H120Z" />
          </svg>
        </Link>
        <Link to="/">
          <h1 className="logo-nav">DESIGNER.AL</h1>
        </Link>
      </div>
      <div className={navClass}>
        <div
          className="navbar-item"
          onMouseEnter={() => handleMouseEnter("new")}
          onMouseLeave={() => handleMouseLeave("new")}
        >
          New{" "}
          <svg
            xmlns="http://www.w3.org/2000/svg"
            height="24px"
            viewBox="0 -960 960 960"
            width="24px"
            fill="#5f6368"
          >
            <path d="M480-344 240-584l56-56 184 184 184-184 56 56-240 240Z" />
          </svg>
        </div>
        <div
          className="navbar-item"
          onMouseEnter={() => handleMouseEnter("brands")}
          onMouseLeave={() => handleMouseLeave("brands")}
        >
          Brands{" "}
          <svg
            xmlns="http://www.w3.org/2000/svg"
            height="24px"
            viewBox="0 -960 960 960"
            width="24px"
            fill="#5f6368"
          >
            <path d="M480-344 240-584l56-56 184 184 184-184 56 56-240 240Z" />
          </svg>
        </div>
        <div
          className="navbar-item"
          onMouseEnter={() => handleMouseEnter("bags")}
          onMouseLeave={() => handleMouseLeave("bags")}
        >
          Bags{" "}
          <svg
            xmlns="http://www.w3.org/2000/svg"
            height="24px"
            viewBox="0 -960 960 960"
            width="24px"
            fill="#5f6368"
          >
            <path d="M480-344 240-584l56-56 184 184 184-184 56 56-240 240Z" />
          </svg>
        </div>
        <div
          className="navbar-item"
          onMouseEnter={() => handleMouseEnter("accessories")}
          onMouseLeave={() => handleMouseLeave("accessories")}
        >
          Accessories{" "}
          <svg
            xmlns="http://www.w3.org/2000/svg"
            height="24px"
            viewBox="0 -960 960 960"
            width="24px"
            fill="#5f6368"
          >
            <path d="M480-344 240-584l56-56 184 184 184-184 56 56-240 240Z" />
          </svg>
        </div>
        <div
          className="navbar-item"
          onMouseEnter={() => handleMouseEnter("fragrances")}
          onMouseLeave={() => handleMouseLeave("fragrances")}
        >
          Fragrances{" "}
          <svg
            xmlns="http://www.w3.org/2000/svg"
            height="24px"
            viewBox="0 -960 960 960"
            width="24px"
            fill="#5f6368"
          >
            <path d="M480-344 240-584l56-56 184 184 184-184 56 56-240 240Z" />
          </svg>
        </div>
        <div
          className="navbar-item"
          onMouseEnter={() => handleMouseEnter("sale")}
          onMouseLeave={() => handleMouseLeave("sale")}
        >
          Sale{" "}
          <svg
            xmlns="http://www.w3.org/2000/svg"
            height="24px"
            viewBox="0 -960 960 960"
            width="24px"
            fill="#5f6368"
          >
            <path d="M480-344 240-584l56-56 184 184 184-184 56 56-240 240Z" />
          </svg>
        </div>
        <div className="navbar-item">
          <Link to="/contact">Contact Us</Link>
        </div>

        <div className="icon-container">
          <div className="search-container navbar-item" ref={searchRef}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="24px"
              viewBox="0 -960 960 960"
              width="24px"
              fill="#5f6368"
            >
              <path
                d="M784-120 532-372q-30 24-69 38t-83 14q-109 0-184.5-75.5T120-580q0-109 75.5-184.5T380-840q109 0 184.5 75.5T640-580q0 44-14 83t-38 69l252 252-56 56ZM380-400q75 0 127.5-52.5T560-580q0-75-52.5-127.5T380-760q-75 0-127.5 52.5T200-580q0 75 52.5 127.5T380-400Z"
                onClick={toggleSearch}
              />
            </svg>
            {isSearchOpen && (
               <input
               type="text"
               placeholder="Search for products..."
               className={`search-input ${isSearchOpen ? "open" : ""}`}
               value={searchQuery}
               onChange={(e) => setSearchQuery(e.target.value)}
               autoFocus
             />
            )}
          </div>

          <Link to="/cart" className="cart-icon navbar-item">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="24px"
              viewBox="0 -960 960 960"
              width="24px"
              fill="#5f6368"
            >
              <path d="M240-80q-33 0-56.5-23.5T160-160v-480q0-33 23.5-56.5T240-720h80q0-66 47-113t113-47q66 0 113 47t47 113h80q33 0 56.5 23.5T800-640v480q0 33-23.5 56.5T720-80H240Zm0-80h480v-480h-80v80q0 17-11.5 28.5T600-520q-17 0-28.5-11.5T560-560v-80H400v80q0 17-11.5 28.5T360-520q-17 0-28.5-11.5T320-560v-80h-80v480Zm160-560h160q0-33-23.5-56.5T480-800q-33 0-56.5 23.5T400-720ZM240-160v-480 480Z" />
            </svg>
            <span className="cart-count">{cartCount}</span>
          </Link>
        </div>
      </div>

      <div
        className={`dropdown-container ${
          hoveredItem === "new" ? "active" : ""
        }`}
        onMouseEnter={() => setHoveredItem("new")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column dropdown-image-left">
            <Link to="">
              <img
                src="https://www.pradagroup.com/content/dam/pradagroup/immagini/newsandmedia/2019-news/double-exposure-adv-campaign/immagini/Cover.jpg"
                alt="Prada"
              ></img>{" "}
            </Link>
          </div>
        </div>
      </div>
      <div
        className={`dropdown-container ${
          hoveredItem === "brands" ? "active" : ""
        }`}
        onMouseEnter={() => setHoveredItem("brands")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column">
            <a href="#">Chanel</a>
            <a href="#">Dior</a>
            <a href="#">Gucci</a>
            <a href="#">Yves Saint Laurent</a>
            <a href="#">Marc Jacobs</a>
          </div>
          <div className="dropdown-column">
            <a href="#">Burberry</a>
            <a href="#">Prada</a>
            <a href="#">Valentino</a>
            <a href="#">Hugo</a>
          </div>
          <div className="dropdown-column">
            <a href="#">Calvin Klein</a>
            <a href="#">UGG</a>
            <a href="#">Guess</a>
            <a href="#">Steve Madden</a>
          </div>
        </div>
      </div>
      <div
        className={`dropdown-container ${
          hoveredItem === "bags" ? "active" : ""
        }`}
        onMouseEnter={() => setHoveredItem("bags")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column">
            <a href="#">Backpacks</a>
            <a href="#">Shoulder Bags</a>
            <a href="#">Belt Bags</a>
            <a href="#">Laptop Bags</a>
          </div>
          <div className="dropdown-column">
            <a href="#">Tote Bags</a>
            <a href="#">Mini Bags</a>
            <a href="#">Bucket Bags</a>
            <a href="#">Evening Bags</a>
          </div>
          <div className="dropdown-image">
            <img
              src="https://images.prestigeonline.com/wp-content/uploads/sites/4/2021/05/12001602/BVLGARI_210212_HCOMTE_020_301_zl-950x1024.jpg"
              alt="Bags "
            />
          </div>
        </div>{" "}
      </div>
      <div
        className={`dropdown-container ${
          hoveredItem === "accessories" ? "active" : ""
        }`}
        onMouseEnter={() => setHoveredItem("accessories")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column">
            <a href="#">Belts</a>
            <a href="#">Hats</a>
            <a href="#">Scarves</a>
            <a href="#">Sunglasses</a>
          </div>
          <div className="dropdown-column">
            <a href="#">Socks</a>
            <a href="#">Cardholders and wallets</a>
          </div>
          <div className="dropdown-image">
            <img
              src="https://greatsouthernsunnies.com.au/cdn/shop/articles/Tips-on-Buying-Designer-Sunglasses-for-Men.jpg?v=1700221936"
              alt="Sunglasses"
            />
          </div>
        </div>{" "}
      </div>
      <div
        className={`dropdown-container ${
          hoveredItem === "fragrances" ? "active" : ""
        }`}
        onMouseEnter={() => setHoveredItem("fragrances")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column">
            <a href="#">Men's</a>
            <a href="#">Aftershave</a>
            <a href="#">Women's</a>
            <a href="#">Body Mist</a>
          </div>
          <div className="dropdown-column">
            <a href="#">Unisex</a>
            <a href="#">Gift Sets</a>
            <a href="#">Travel Sizes</a>
          </div>
          <div className="dropdown-image">
            <img
              src="https://static01.nyt.com/images/2021/05/10/t-magazine/fashion/Beauty-extra-still-slide-FTPZ-copy/Beauty-extra-still-slide-FTPZ-videoSixteenByNineJumbo1600.jpg"
              alt="Parfumes"
            />
          </div>
        </div>{" "}
      </div>
      <div
        className={`dropdown-container ${
          hoveredItem === "sale" ? "active" : ""
        }`}
        onMouseEnter={() => setHoveredItem("sale")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column dropdown-image-left">
            <Link to="">
              <img
                src="https://images.unsplash.com/photo-1531303435785-3853ba035cda?q=80&w=1740&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
                alt="Sale"
              />
            </Link>
          </div>
        </div>{" "}
      </div>
    </div>
  );
};

export default NavigationBar;
