import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./ShoppingCart.css";
import NavigationBar from "../NavigationBar/NavigationBar";
import Footer from "../Footer/Footer";
import { v4 as uuidv4 } from "uuid";

const ShoppingCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  const getUserId = () => {
    let userId = localStorage.getItem("userId");
    if (!userId) {
      userId = uuidv4();
      localStorage.setItem("userId", userId);
    }
    return userId;
  };

  useEffect(() => {
    fetchCartItems();
  }, []);

  const fetchCartItems = async () => {
    try {
      const userId = getUserId();
      console.log("Fetching cart for user:", userId);
      const response = await axios.get(`http://localhost:5000/api/cart/${userId}`);
      const items = response.data.items || [];
      
      // Fetch product details for each item
      const detailedItems = await Promise.all(items.map(item => fetchProductDetails(item._id, item.quantity)));
      
      setCartItems(detailedItems);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching cart items:", error);
      setError("Failed to fetch cart items: " + (error.response ? error.response.data.message : error.message));
      setLoading(false);
    }
  };

  const fetchProductDetails = async (productId, quantity) => {
    try {
      const response = await axios.get(`http://localhost:5000/api/products/${productId}`); // Update this URL to your actual product API endpoint
      const product = response.data;

      return {
        ...product,
        quantity, // Include the quantity from the cart
      };
    } catch (error) {
      console.error("Error fetching product details:", error);
      return null; // Return null if product details can't be fetched
    }
  };

  const handleRemoveItem = async (productId) => {
    try {
      const userId = getUserId();
      await axios.delete(`http://localhost:5000/api/cart/${userId}/item/${productId}`);
      fetchCartItems();
    } catch (err) {
      setError("Failed to remove item");
    }
  };

  const updateCartItem = async (productId, newQuantity) => {
    try {
      const userId = getUserId();
      const quantity = parseInt(newQuantity, 10);

      // Ensure the quantity is valid
      if (quantity < 1) {
        console.error("Quantity must be at least 1");
        return;
      }

      await axios.put(`http://localhost:5000/api/cart/${userId}/item/${productId}`, { quantity });
      fetchCartItems(); // Refresh cart after update
    } catch (error) {
      console.error("Error updating cart item:", error.response?.data || error.message);
    }
  };

  const clearCart = async () => {
    try {
      const userId = getUserId();
      await axios.delete(`http://localhost:5000/api/cart/${userId}`);
      fetchCartItems();
    } catch (err) {
      setError("Failed to clear cart");
    }
  };

  const getTotalPrice = () => {
    return cartItems.reduce((total, item) => total + item.price * item.quantity, 0);
  };

  if (loading) return <div>Loading... Please wait.</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <NavigationBar />
      <div className="cart-page">
        <div className="cart-items-container">
          {cartItems.length === 0 ? (
            <div className="empty-cart">Your cart is empty.</div>
          ) : (
            <table className="cart-table">
              <thead>
                <tr>
                  <th>Product</th>
                  <th>Price</th>
                  <th>Quantity</th>
                  <th>Subtotal</th>
                  <th>Actions</th>
                </tr>
              </thead>
              <tbody>
                {cartItems.map((item) => (
                  <tr key={item._id} className="cart-item-row">
                    <td className="cart-item-info">
                      <img src={item.image} alt={item.name} className="cart-item-image" />
                      <div className="cart-item-details">
                        <h3>{item.name}</h3>
                        <p>Size: UNI</p>
                        <p>Color: {item.color}</p>
                      </div>
                    </td>
                    <td className="cart-item-price">{item.price} Lek</td>
                    <td className="cart-item-quantity">
                      <input
                        type="number"
                        min="1"
                        defaultValue={item.quantity}
                        onChange={(e) => updateCartItem(item._id, e.target.value)}
                      />
                    </td>
                    <td className="cart-item-subtotal">
                      {(item.price * item.quantity).toLocaleString()} Lek
                    </td>
                    <td className="cart-item-actions">
                      <button onClick={() => handleRemoveItem(item._id)}>✖</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
          <div className="cart-summary-container">
            <div className="cart-summary">
              <h3>Subtotal: {getTotalPrice().toLocaleString()} Lek</h3>
              <p>Shipping: 0.00 Lek</p>
              <h3>Total Order Amount: {getTotalPrice().toLocaleString()} Lek</h3>
              <Link to="/checkout" className="checkout-btn">PROCEED TO CHECKOUT</Link>
            </div>
            <div className="cart-actions">
              <button className="cart-clear-btn" onClick={clearCart}>Clear Shopping Cart</button>
              <Link to="/products" className="continue-shopping-btn">Continue Shopping</Link>
            </div>
          </div>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default ShoppingCart;
