import React, { useState, useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import axios from "axios";
import "./OneProduct.css";
import Footer from "../Footer/Footer";
import NavigationBar from "../NavigationBar/NavigationBar";
import Slider from "react-slick";
import { v4 as uuidv4 } from "uuid";

const OneProduct = () => {
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [quantity, setQuantity] = useState(1);
  const [suggestions, setSuggestions] = useState([]);
  const { category, subCategory, id } = useParams();

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const baseUrl =
          process.env.REACT_APP_API_URL || "http://localhost:5000";
        const url = `${baseUrl}/api/products/${category}/${subCategory}/${id}`;
        const response = await axios.get(url);
        setProduct(response.data);
        setLoading(false);
      } catch (err) {
        setError(`Failed to fetch product: ${err.message}`);
        setLoading(false);
      }
    };

    const fetchSuggestions = async () => {
      try {
        const baseUrl =
          process.env.REACT_APP_API_URL || "http://localhost:5000";
        const response = await axios.get(`${baseUrl}/api/products/suggestions`);
        setSuggestions(response.data);
      } catch (err) {
        console.error("Failed to fetch suggestions:", err);
      }
    };

    fetchProduct();
    fetchSuggestions();
  }, [category, subCategory, id]);

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;
  if (!product) return <div>Product not found</div>;

  const addToCart = async () => {
    try {
      const userId = getUserId();
      await axios.post(`http://localhost:5000/api/cart/${userId}/add`, {
        productId: product._id,
        quantity: quantity,
      });
    } catch (error) {
      console.error("Error adding product to cart:", error);
    }
  };

  const carouselSettings = {
    dots: true,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    responsive: [
      {
        breakpoint: 1024,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: true,
        },
      },
      {
        breakpoint: 600,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
        },
      },
    ],
  };

  const getUserId = () => {
    let userId = localStorage.getItem("userId");
    if (!userId) {
      userId = uuidv4();
      localStorage.setItem("userId", userId);
    }
    return userId;
  };

  return (
    <div className="one-product-page">
      <NavigationBar />
      <div className="product-details">
        <img src={product.image} alt={product.name} className="product-image" />
        <div className="product-info">
          <h1 className="product-name">{product.name}</h1>
          <p className="product-price">${product.price.toFixed(2)}</p>
          <p className="product-description">{product.description}</p>
          <div className="quantity-controls">
            <button onClick={() => setQuantity(Math.max(quantity - 1, 1))}>-</button>
            <span>{quantity}</span>
            <button onClick={() => setQuantity(quantity + 1)}>+</button>
          </div>
          <button className="add-to-cart-btn" onClick={addToCart}>
            Add to Cart
          </button>
        </div>
      </div>

      <section className="suggestions-section">
        <h2>Similar Products</h2>
        <Slider {...carouselSettings}>
          {suggestions.map((suggestedProduct) => (
            <div key={suggestedProduct._id} className="suggestion-item">
              <Link to={`/products/${suggestedProduct.category}/${suggestedProduct.subCategory}/${suggestedProduct._id}`}>
                <img src={suggestedProduct.image} alt={suggestedProduct.name} className="suggestion-image" />
                <h3>{suggestedProduct.name}</h3>
                <p>${suggestedProduct.price.toFixed(2)}</p>
              </Link>
            </div>
          ))}
        </Slider>
      </section>

      <Footer />
    </div>
  );
};

export default OneProduct;
