import React, { useState, useEffect } from "react";
import { useNavigate, useLocation } from "react-router-dom";
import axios from 'axios';
import "./Checkout.css";
import NavigationBar from "../NavigationBar/NavigationBar";
import Footer from "../Footer/Footer";

const Checkout = () => {
  const navigate = useNavigate();
  const location = useLocation();
  const { cartItems, totalPrice } = location.state;
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    address: "",
    city: "",
    postalCode: "",
    phoneNumber: "",
  });

  const [paymentMethod, setPaymentMethod] = useState('credit-card');
  const [errors, setErrors] = useState({});

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({ ...formData, [name]: value });
  };

  const validateForm = () => {
    let formErrors = {};
    if (!formData.name) formErrors.name = "Name is required";
    if (!formData.email) formErrors.email = "Email is required";
    if (!formData.address) formErrors.address = "Address is required";
    if (!formData.city) formErrors.city = "City is required";
    if (!formData.postalCode) formErrors.postalCode = "Postal code is required";
    if (!formData.phoneNumber) formErrors.phoneNumber = "Phone number is required";
    return formErrors;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const validationErrors = validateForm();
    if (Object.keys(validationErrors).length > 0) {
      setErrors(validationErrors);
    } else {
      try {
        const response = await axios.post("/api/orders/checkout", {
          cartItems,
          totalPrice,
          shippingDetails: formData,
          paymentMethod,
        });
        if (response.status === 200) {
          navigate(`/order-confirmation/${response.data.orderId}`);
        } else {
          alert("Failed to place order");
        }
      } catch (error) {
        console.error("Error submitting order:", error);
        alert("Error submitting order");
      }
    }
  };

  return (
    <div className="checkout-container">
      <NavigationBar />
      <div className="checkout-page">
        <h2 className="checkout-title">Checkout</h2>

        <div className="checkout-content">
          <div className="cart-summary">
            <h3 className="section-title">Order Summary</h3>
            {cartItems.map((item) => (
              <div key={item.id} className="cart-item">
                <span className="item-name">{item.name}</span>
                <span className="item-details">
                  {item.price} Lek x {item.quantity}
                </span>
              </div>
            ))}
            <div className="cart-total">
              <h4>Total: <span>{totalPrice} Lek</span></h4>
            </div>
          </div>

          <form onSubmit={handleSubmit} className="checkout-form">
            <h3 className="section-title">Shipping Information</h3>
            {Object.entries(formData).map(([key, value]) => (
              <div key={key} className="form-group">
                <label htmlFor={key}>{key.charAt(0).toUpperCase() + key.slice(1)}</label>
                <input
                  type={key === 'email' ? 'email' : 'text'}
                  id={key}
                  name={key}
                  value={value}
                  onChange={handleInputChange}
                  className={errors[key] ? 'error' : ''}
                />
                {errors[key] && <p className="error-message">{errors[key]}</p>}
              </div>
            ))}

            <h3 className="section-title">Payment Method</h3>
            <div className="payment-methods">
              {['credit-card', 'paypal'].map((method) => (
                <label key={method} className="payment-method">
                  <input
                    type="radio"
                    name="paymentMethod"
                    value={method}
                    checked={paymentMethod === method}
                    onChange={(e) => setPaymentMethod(e.target.value)}
                  />
                  <span>{method === 'credit-card' ? 'Credit Card' : 'PayPal'}</span>
                </label>
              ))}
            </div>

            <button type="submit" className="place-order-btn">Place Order</button>
          </form>
        </div>
      </div>
      <Footer />
    </div>
  );
};

export default Checkout;
