import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
import { Container, Row, Col, Image } from "react-bootstrap";
import "./MainGrid.css";
import axios from "axios";

const MainGrid = () => {
  const [brands, setBrands] = useState([]);
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchBrands = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/brands");
        setBrands(response.data);
      } catch (err) {
        setError("Failed to load brands");
      }
    };

    fetchBrands();
  }, []);

  if (error) {
    return <div>{error}</div>;
  }

  // Shuffle brands and take the first 4
  const shuffledBrands = [...brands].sort(() => 0.5 - Math.random()).slice(0, 4);

  return (
    <Container className="mt-4 image-grid">
      <Row>
        <Col md={6} className="left-image custom-col">
          <Link to={`/brands/${shuffledBrands[0]._id}`} className="image-link">
            <Image
              src={shuffledBrands[0].images[0]} // Using the first image as the display image
              alt={shuffledBrands[0].name}
              fluid
              className="big-image"
            />
          </Link>
        </Col>
        <Col md={6} className="right-images custom-col">
          <div className="top-images">
            <Link to={`/brands/${shuffledBrands[1]._id}`} className="image-link">
              <div className="top-image">
                <Image
                  src={shuffledBrands[1].images[0]}
                  alt={shuffledBrands[1].name}
                  fluid
                  className="small-image"
                />
              </div>
            </Link>
            <Link to={`/brands/${shuffledBrands[2]._id}`} className="image-link">
              <div className="top-image">
                <Image
                  src={shuffledBrands[2].images[0]}
                  alt={shuffledBrands[2].name}
                  fluid
                  className="small-image"
                />
              </div>
            </Link>
          </div>
          <Link to={`/brands/${shuffledBrands[3]._id}`} className="image-link">
            <div className="bottom-image">
              <Image
                src={shuffledBrands[3].images[0]}
                alt={shuffledBrands[3].name}
                fluid
                className="small-image"
              />
            </div>
          </Link>
        </Col>
      </Row>
    </Container>
  );
};

export default MainGrid;
