import React, { useState, useEffect, useRef } from "react";
import { Link } from "react-router-dom";
import "./NavigationBar.css";

const NavigationBar = ({ cartItemsCount }) => {
  const [hoveredItem, setHoveredItem] = useState(null);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [navbarOpen, setNavbarOpen] = useState(false);
  const [navClass, setNavClass] = useState("navbar-black");
  const [searchQuery, setSearchQuery] = useState("");
  const [searchResults, setSearchResults] = useState([]);
  const [isDropdownVisible, setIsDropdownVisible] = useState(false);
  const [mobileMenuState, setMobileMenuState] = useState("main");
  const [isMobile, setIsMobile] = useState(window.innerWidth <= 768);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setIsDropdownVisible(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  const handleSearch = async (query) => {
    setSearchQuery(query);
    if (query.length > 2) {
      try {
        const response = await fetch(`/api/products/search?q=${query}`);
        const data = await response.json();
        setSearchResults(data);
        setIsDropdownVisible(true);
      } catch (error) {
        console.error("Error fetching search results:", error);
      }
    } else {
      setSearchResults([]);
      setIsDropdownVisible(false);
    }
  };

  const searchRef = useRef(null);

  const toggleSearch = () => setIsSearchOpen(!isSearchOpen);

  const toggleNavbar = () => {
    setNavbarOpen(!navbarOpen);
    setNavClass(navbarOpen ? "hidden" : "show");
    setMobileMenuState("main");
  };

  const handleMobileMenuClick = (category) => {
    setMobileMenuState(category);
  };

  const handleMouseEnter = (item) => {
    setHoveredItem(item);
  };

  const handleMouseLeave = (item) => {
    if (hoveredItem === item) {
      setHoveredItem(null);
    }
  };

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (searchRef.current && !searchRef.current.contains(event.target)) {
        setIsSearchOpen(false);
      }
    };
    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, [searchRef]);

  const renderMobileMenu = () => {
    switch (mobileMenuState) {
      case "main":
        return (
          <>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("new")}>New</div>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("brands")}>Brands</div>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("bags")}>Bags</div>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("accessories")}>Accessories</div>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("fragrances")}>Fragrances</div>
            <div className="navbar-item" onClick={() => handleMobileMenuClick("sale")}>Sale</div>
            <div className="navbar-item">
              <Link to="/contact">Contact Us</Link>
            </div>
          </>
        );
      case "new":
        return (
          <div className="navbar-item">
            <Link to="/products/new">New Arrivals</Link>
          </div>
        );
      case "brands":
        return (
          <>
            <div className="navbar-item"><Link to="/brands/chanel">Chanel</Link></div>
            <div className="navbar-item"><Link to="/brands/dior">Dior</Link></div>
            <div className="navbar-item"><Link to="/brands/gucci">Gucci</Link></div>
            <div className="navbar-item"><Link to="/brands/ysl">Yves Saint Laurent</Link></div>
            <div className="navbar-item"><Link to="/brands/marc-jacobs">Marc Jacobs</Link></div>
            <div className="navbar-item"><Link to="/brands/burberry">Burberry</Link></div>
            <div className="navbar-item"><Link to="/brands/prada">Prada</Link></div>
            <div className="navbar-item"><Link to="/brands/valentino">Valentino</Link></div>
            <div className="navbar-item"><Link to="/brands/hugo">Hugo</Link></div>
            <div className="navbar-item"><Link to="/brands/calvin-klein">Calvin Klein</Link></div>
            <div className="navbar-item"><Link to="/brands/ugg">UGG</Link></div>
            <div className="navbar-item"><Link to="/brands/guess">Guess</Link></div>
            <div className="navbar-item"><Link to="/brands/steve-madden">Steve Madden</Link></div>
          </>
        );
      case "bags":
        return (
          <>
            <div className="navbar-item"><Link to="/products/bags/backpacks">Backpacks</Link></div>
            <div className="navbar-item"><Link to="/products/bags/crossbody-bags">Crossbody Bags</Link></div>
            <div className="navbar-item"><Link to="/products/bags/belt-bags">Belt Bags</Link></div>
            <div className="navbar-item"><Link to="/products/bags/laptop-bags">Laptop Bags</Link></div>
            <div className="navbar-item"><Link to="/products/bags/tote-bags">Tote Bags</Link></div>
            <div className="navbar-item"><Link to="/products/bags/mini-bags">Mini Bags</Link></div>
            <div className="navbar-item"><Link to="/products/bags/bucket-bags">Bucket Bags</Link></div>
            <div className="navbar-item"><Link to="/products/bags/evening-bags">Evening Bags</Link></div>
          </>
        );
      case "accessories":
        return (
          <>
            <div className="navbar-item"><Link to="/products/accessories/belts">Belts</Link></div>
            <div className="navbar-item"><Link to="/products/accessories/hats">Hats and Caps</Link></div>
            <div className="navbar-item"><Link to="/products/accessories/scarves">Scarves</Link></div>
            <div className="navbar-item"><Link to="/products/accessories/sunglasses">Sunglasses</Link></div>
            <div className="navbar-item"><Link to="/products/accessories/socks">Socks</Link></div>
            <div className="navbar-item"><Link to="/products/accessories/wallets">Wallets</Link></div>
            <div className="navbar-item"><Link to="/products/accessories/underwear">Underwear</Link></div>
            <div className="navbar-item"><Link to="/products/accessories/shoes">Shoes</Link></div>
          </>
        );
      case "fragrances":
        return (
          <>
            <div className="navbar-item"><Link to="/products/fragrances/for-him">For Him</Link></div>
            <div className="navbar-item"><Link to="/products/fragrances/aftershave">Aftershave</Link></div>
            <div className="navbar-item"><Link to="/products/fragrances/for-her">For Her</Link></div>
            <div className="navbar-item"><Link to="/products/fragrances/body-mist">Body Mist</Link></div>
            <div className="navbar-item"><Link to="/products/fragrances/unisex">Unisex</Link></div>
            <div className="navbar-item"><Link to="/products/fragrances/gift-sets">Gift Sets</Link></div>
            <div className="navbar-item"><Link to="/products/fragrances/travel-sizes">Travel Sizes</Link></div>
          </>
        );
      case "sale":
        return (
          <div className="navbar-item">
            <Link to="/products/sale">Sale Items</Link>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="navbar-container">
      <div className="navbar-logo">
        <Link className="hamburger-menu" onClick={toggleNavbar}>
          <svg
            xmlns="http://www.w3.org/2000/svg"
            height="24px"
            viewBox="0 -960 960 960"
            width="24px"
            fill="#5f6368"
          >
            <path d="M120-680v-80h720v80H120Zm0 480v-80h720v80H120Zm0-240v-80h720v80H120Z" />
          </svg>
        </Link>
        <Link to="/">
          <h1 className="logo-nav">DESIGNER.AL</h1>
        </Link>
      </div>
      <div className={navClass}>
        {navbarOpen ? renderMobileMenu() : (
          <>
            <div
              className="navbar-item"
              onMouseEnter={() => handleMouseEnter("new")}
              onMouseLeave={() => handleMouseLeave("new")}
            >
              New{" "}
            </div>
            <div
              className="navbar-item"
              onMouseEnter={() => handleMouseEnter("brands")}
              onMouseLeave={() => handleMouseLeave("brands")}
            >
              Brands{" "}

            </div>
            <div
              className="navbar-item"
              onMouseEnter={() => handleMouseEnter("bags")}
              onMouseLeave={() => handleMouseLeave("bags")}
            >
              Bags{" "}
            </div>
            <div
              className="navbar-item"
              onMouseEnter={() => handleMouseEnter("accessories")}
              onMouseLeave={() => handleMouseLeave("accessories")}
            >
              Accessories{" "}
            </div>
            <div
              className="navbar-item"
              onMouseEnter={() => handleMouseEnter("fragrances")}
              onMouseLeave={() => handleMouseLeave("fragrances")}
            >
              Fragrances{" "}

            </div>
            <div
              className="navbar-item"
              onMouseEnter={() => handleMouseEnter("sale")}
              onMouseLeave={() => handleMouseLeave("sale")}
            >
              Sale{" "}

            </div>
            <div className="navbar-item">
              <Link to="/contact">Contact Us</Link>
            </div>
          </>
        )}

        <div className="icon-container">
          <div className="search-container navbar-item" ref={searchRef}>
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="24px"
              viewBox="0 -960 960 960"
              width="24px"
              fill="#5f6368"
            >
              <path
                d="M784-120 532-372q-30 24-69 38t-83 14q-109 0-184.5-75.5T120-580q0-109 75.5-184.5T380-840q109 0 184.5 75.5T640-580q0 44-14 83t-38 69l252 252-56 56ZM380-400q75 0 127.5-52.5T560-580q0-75-52.5-127.5T380-760q-75 0-127.5 52.5T200-580q0 75 52.5 127.5T380-400Z"
                onClick={toggleSearch}
              />
            </svg>
            {isSearchOpen && (
              <div className="search-container" ref={searchRef}>
                <input
                  type="text"
                  placeholder="Search for products..."
                  className="search-input"
                  value={searchQuery}
                  onChange={(e) => handleSearch(e.target.value)}
                  onFocus={() =>
                    searchResults.length > 0 && setIsDropdownVisible(true)
                  }
                />
                {isDropdownVisible && (
                  <div className="search-dropdown">
                    {searchResults.length > 0 ? (
                      searchResults.map((product) => (
                        <Link
                          to={`/product/${product._id}`}
                          key={product._id}
                          className="search-result-item"
                        >
                          <img
                            src={product.image}
                            alt={product.name}
                            className="search-result-image"
                          />
                          <span>{product.name}</span>
                        </Link>
                      ))
                    ) : (
                      <div className="no-results">No products found</div>
                    )}
                  </div>
                )}
              </div>
            )}
          </div>

          <Link to="/cart" className="cart-icon navbar-item">
            <svg
              xmlns="http://www.w3.org/2000/svg"
              height="24px"
              viewBox="0 -960 960 960"
              width="24px"
              fill="#5f6368"
            >
              <path d="M240-80q-33 0-56.5-23.5T160-160v-480q0-33 23.5-56.5T240-720h80q0-66 47-113t113-47q66 0 113 47t47 113h80q33 0 56.5 23.5T800-640v480q0 33-23.5 56.5T720-80H240Zm0-80h480v-480h-80v80q0 17-11.5 28.5T600-520q-17 0-28.5-11.5T560-560v-80H400v80q0 17-11.5 28.5T360-520q-17 0-28.5-11.5T320-560v-80h-80v480Zm160-560h160q0-33-23.5-56.5T480-800q-33 0-56.5 23.5T400-720ZM240-160v-480 480Z" />
            </svg>
            <span className="cart-count">{cartItemsCount}</span>
          </Link>
        </div>
      </div>

      <div
        className={`dropdown-container ${hoveredItem === "new" ? "active" : ""
          }`}
        onMouseEnter={() => setHoveredItem("new")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column dropdown-image-left">
            <Link to="/products/new">
              <img
                src="https://www.pradagroup.com/content/dam/pradagroup/immagini/newsandmedia/2019-news/double-exposure-adv-campaign/immagini/Cover.jpg"
                alt="Prada"
              ></img>{" "}
            </Link>
          </div>
        </div>
      </div>
      <div
        className={`dropdown-container ${hoveredItem === "brands" ? "active" : ""
          }`}
        onMouseEnter={() => setHoveredItem("brands")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column">
            <Link to="/brands/chanel">Chanel</Link>
            <Link to="/brands/dior">Dior</Link>
            <Link to="/brands/gucci">Gucci</Link>
            <Link to="/brands/ysl">Yves Saint Laurent</Link>
            <Link to="/brands/marc-jacobs">Marc Jacobs</Link>
          </div>
          <div className="dropdown-column">
            <Link to="/brands/burberry">Burberry</Link>
            <Link to="/brands/prada">Prada</Link>
            <Link to="/brands/valentino">Valentino</Link>
            <Link to="/brands/hugo">Hugo</Link>
          </div>
          <div className="dropdown-column">
            <Link to="/brands/calvin-klein">Calvin Klein</Link>
            <Link to="/brands/ugg">UGG</Link>
            <Link to="/brands/guess">Guess</Link>
            <Link to="/brands/steve-madden">Steve Madden</Link>
          </div>
        </div>
      </div>
      <div
        className={`dropdown-container ${hoveredItem === "bags" ? "active" : ""
          }`}
        onMouseEnter={() => setHoveredItem("bags")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column">
            <Link to="/products/bags/backpacks">Backpacks</Link>
            <Link to="/products/bags/crossbody-bags">Crossbody Bags</Link>
            <Link to="/products/bags/belt-bags">Belt Bags</Link>
            <Link to="/products/bags/laptop-bags">Laptop Bags</Link>
          </div>
          <div className="dropdown-column">
            <Link to="/products/bags/tote-bags">Tote Bags</Link>
            <Link to="/products/bags/mini-bags">Mini Bags</Link>
            <Link to="/products/bags/bucket-bags">Bucket Bags</Link>
            <Link to="/products/bags/evening-bags">Evening Bags</Link>
          </div>
          <div className="dropdown-image">
            <img
              src="https://images.prestigeonline.com/wp-content/uploads/sites/4/2021/05/12001602/BVLGARI_210212_HCOMTE_020_301_zl-950x1024.jpg"
              alt="Bags "
            />
          </div>
        </div>{" "}
      </div>
      <div
        className={`dropdown-container ${hoveredItem === "accessories" ? "active" : ""
          }`}
        onMouseEnter={() => setHoveredItem("accessories")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column">
            <Link to="/products/accessories/belts">Belts</Link>
            <Link to="/products/accessories/hats">Hats and Caps</Link>
            <Link to="/products/accessories/scarves">Scarves</Link>
            <Link to="/products/accessories/sunglasses">Sunglasses</Link>
          </div>
          <div className="dropdown-column">
            <Link to="/products/accessories/socks">Socks</Link>
            <Link to="/products/accessories/wallets">Wallets</Link>
            <Link to="/products/accessories/underwear">Underwear</Link>
            <Link to="/products/accessories/shoes">Shoes</Link>
          </div>
          <div className="dropdown-image">
            <img
              src="https://greatsouthernsunnies.com.au/cdn/shop/articles/Tips-on-Buying-Designer-Sunglasses-for-Men.jpg?v=1700221936"
              alt="Sunglasses"
            />
          </div>
        </div>{" "}
      </div>
      <div
        className={`dropdown-container ${hoveredItem === "fragrances" ? "active" : ""
          }`}
        onMouseEnter={() => setHoveredItem("fragrances")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column">
            <Link to="/products/fragrances/for-him">For Him</Link>
            <Link to="/products/fragrances/aftershave">Aftershave</Link>
            <Link to="/products/fragrances/for-her">For Her</Link>
            <Link to="/products/fragrances/body-mist">Body Mist</Link>
          </div>
          <div className="dropdown-column">
            <Link to="/products/fragrances/unisex">Unisex</Link>
            <Link to="/products/fragrances/gift-sets">Gift Sets</Link>
            <Link to="/products/fragrances/travel-sizes">Travel Sizes</Link>
          </div>
          <div className="dropdown-image">
            <img
              src="https://static01.nyt.com/images/2021/05/10/t-magazine/fashion/Beauty-extra-still-slide-FTPZ-copy/Beauty-extra-still-slide-FTPZ-videoSixteenByNineJumbo1600.jpg"
              alt="Parfumes"
            />
          </div>
        </div>{" "}
      </div>
      <div
        className={`dropdown-container ${hoveredItem === "sale" ? "active" : ""
          }`}
        onMouseEnter={() => setHoveredItem("sale")}
        onMouseLeave={() => setHoveredItem(null)}
      >
        <div className="dropdown-content">
          <div className="dropdown-column dropdown-image-left">
            <Link to="/products/sale">
              <img
                src="https://images.unsplash.com/photo-1531303435785-3853ba035cda?q=80&w=1740&auto=format&fit=crop&ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D"
                alt="Sale"
              />
            </Link>
          </div>
        </div>{" "}
      </div>
    </div>
  );
};

export default NavigationBar;
