import React, { useState } from "react";
import { Container, Form, Button, Alert } from "react-bootstrap";
import axios from "axios";
import "./LogIn.css";

const LogIn = ({ onLoginSuccess }) => {
    const [userLog, setUserLog] = useState({
        email: "",
        password: "",
    });
    const [error, setError] = useState("");

    const handleChange = (e) => {
        setUserLog({ ...userLog, [e.target.name]: e.target.value });
    };

    const handleSubmit = async (e) => {
        e.preventDefault();
        try {
            const response = await axios.post(
                "http://localhost:5000/api/user/login",
                { email: userLog.email, password: userLog.password }
            );
            if (response.data.token) {
                localStorage.setItem('token', response.data.token);
                localStorage.setItem(
                    "userInfo",
                    JSON.stringify({
                        id: response.data.id,
                        email: response.data.email,
                        username: response.data.username,
                    })
                );
                onLoginSuccess(); // Call the callback function
            } else {
                setError("Login successful but no token received. Please try again.");
            }
        } catch (error) {
            console.error("Login error:", error);
            if (error.response) {
                // The request was made and the server responded with a status code
                // that falls out of the range of 2xx
                setError(`Login failed: ${error.response.data.message || error.response.data}`);
            } else if (error.request) {
                // The request was made but no response was received
                setError("No response received from server. Please try again later.");
            } else {
                // Something happened in setting up the request that triggered an Error
                setError(`Error: ${error.message}`);
            }
        }
    };

    return (
        <div className="login-page">
            <Container className="login-container">
                <Form className="login-form" onSubmit={handleSubmit}>
                    <Form.Group className="login-form-group">
                        <Form.Label className="login-form-label">Email</Form.Label>
                        <Form.Control
                            className="login-form-control"
                            type="email"
                            onChange={handleChange}
                            name="email"
                            value={userLog.email}
                            placeholder="Enter email"
                        />
                    </Form.Group>
                    <Form.Group className="login-form-group">
                        <Form.Label className="login-form-label">Password</Form.Label>
                        <Form.Control
                            className="login-form-control"
                            type="password"
                            name="password"
                            onChange={handleChange}
                            value={userLog.password}
                            placeholder="Enter password"
                        />
                    </Form.Group>
                    <Button
                        className="login-submit-btn"
                        variant="primary"
                        type="submit"
                    >
                        Login
                    </Button>
                </Form>
                {error && <Alert variant="danger" className="login-alert">{error}</Alert>}
            </Container>
        </div>
    );
};

export default LogIn;
