import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './Cards.css';

const Cards = () => {
  const [products, setProducts] = useState([]);

  const defaultProducts = [
    {
      _id: 'default1',
      name: 'Default 1',
      price: 10.00,
      image: 'https://via.placeholder.com/300',
    },
    {
      _id: 'default2',
      name: 'Default 2',
      price: 20.00,
      image: 'https://via.placeholder.com/300',
    },
    {
      _id: 'default3',
      name: 'Default 3',
      price: 30.00,
      image: 'https://via.placeholder.com/300',
    },
    {
      _id: 'default4',
      name: 'Default 4',
      price: 76.00,
      image: 'https://via.placeholder.com/300',
    },
  ];

  useEffect(() => {
    axios.get('/api/products')
      .then(response => {
        if (response.data && response.data.length > 0) {
          setProducts(response.data);
        } else {
          setProducts(defaultProducts); 
        }
      })
      .catch(error => {
        console.error("Error fetching products:", error);
        setProducts(defaultProducts); 
      });
  }, []);

  const handleAddToCart = (product) => {
    axios.post('/api/cart', { productId: product._id })
      .then(response => alert(`${product.name} added to cart!`))
      .catch(error => console.error("Error adding product to cart:", error));
  };

  return (
    <div>
    <h3 className='favorites-heading'>Best seller</h3>
    <div className="favorites-container">      {products.map((product) => (
        <div key={product._id} className="card">
          <img 
            src={product.image || 'https://via.placeholder.com/300'} 
            alt={product.name || 'Default Product'} 
            className="card-img" 
          />
          <div className="card-content">
            <h3 className="card-title">{product.name || 'Name Unavailable'}</h3>
            <p className="card-price">${product.price?.toFixed(2) || '0.00'}</p>
            <button className="btn-add-to-cart" onClick={() => handleAddToCart(product)}>
              Add to Cart
            </button>
          </div>
        </div>
      ))}
    </div>
    </div>
  );
};

export default Cards;
