import React, { useState, useEffect } from 'react';
import { useNavigate, Navigate } from 'react-router-dom';
import './UserProfile.css';
import LogIn from './LogIn';

const UserProfile = () => {
    const [userInfo, setUserInfo] = useState(null);
    const navigate = useNavigate();

    useEffect(() => {
        const storedUserInfo = localStorage.getItem('userInfo');
        if (storedUserInfo) {
            setUserInfo(JSON.parse(storedUserInfo));
        } else {
            navigate('/login');
        }
    }, [navigate]);

    const handleLogout = () => {
        localStorage.removeItem('userInfo');
        localStorage.removeItem('token');
        navigate('/login');
    };

    if (!userInfo) {
        return <Navigate to="/login" replace />;
    }

    return (
        <div className="profile-container">
            <div className="profile-card">
                <div className="profile-header">
                    <img src={userInfo.avatar || 'https://via.placeholder.com/100'} alt="Profile" className="profile-avatar" />
                    <div className="profile-title">
                        <h2>{userInfo.name || 'User Name'}</h2>
                        <p className="user-title">{userInfo.email || 'user@example.com'}</p>
                    </div>
                </div>
                <div className="profile-info">
                    <div className="info-item">
                        <h3>Account Details</h3>
                        <p>Member since: {userInfo.memberSince || 'N/A'}</p>
                        <p>Account type: {userInfo.accountType || 'Standard'}</p>
                    </div>
                    <div className="info-item">
                        <h3>Shipping Address</h3>
                        <p>{userInfo.shippingAddress || 'No address provided'}</p>
                    </div>
                    <div className="info-item">
                        <h3>Payment Methods</h3>
                        <p>{userInfo.paymentMethod ? `Visa ending in ${userInfo.paymentMethod}` : 'No payment method saved'}</p>
                    </div>
                </div>
                <div className="profile-actions">
                    <button className="action-button edit-profile">Edit Profile</button>
                    <button className="action-button logout-button" onClick={handleLogout}>Logout</button>
                </div>
                <div className="order-history">
                    <h3>Recent Orders</h3>
                    {userInfo.recentOrders && userInfo.recentOrders.length > 0 ? (
                        <ul className="order-list">
                            {userInfo.recentOrders.map(order => (
                                <li key={order.id} className="order-item">
                                    <strong>Order #{order.id}</strong>
                                    <p>Date: {order.date}</p>
                                    <p>Total: ${order.total}</p>
                                    <p>Status: {order.status}</p>
                                </li>
                            ))}
                        </ul>
                    ) : (
                        <p>No recent orders</p>
                    )}
                </div>
            </div>
        </div>
    );
};

export default UserProfile;
