import React, { useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import ShoppingCartOutlinedIcon from "@mui/icons-material/ShoppingCartOutlined";
import "./NavigationBar.css";
import { Container, Nav, Navbar, Form, NavDropdown } from "react-bootstrap";

const NavigationBar = () => {
  const [isNavbarOpen, setIsNavbarOpen] = useState(false);
  const [isSearchOpen, setIsSearchOpen] = useState(false);
  const [searchQuery, setSearchQuery] = useState("");
  const [products, setProducts] = useState([]);

  // Function to toggle navbar visibility
  const toggleNavbar = () => setIsNavbarOpen(!isNavbarOpen);

  // Function to toggle search bar visibility
  const toggleSearch = () => setIsSearchOpen(!isSearchOpen);

  // Function to handle search query and fetch products from the database
  const handleSearch = async (e) => {
    e.preventDefault();

    // Simulating a product fetch (Replace with your actual API call)
    const fakeProducts = [
      {
        id: 1,
        name: "Scotch & Soda Polo",
        price: "7,390.00 Lek",
        image: "/images/scotch-polo.png"
      },
      {
        id: 2,
        name: "HUGO Shirt",
        price: "9,990.00 Lek",
        image: "/images/hugo-shirt.png"
      },
      {
        id: 3,
        name: "Guess Jacket",
        price: "17,490.00 Lek",
        image: "/images/guess-jacket.png"
      }
    ];

    // Set the fetched products to the state
    setProducts(fakeProducts);
  };

  return (
    <Navbar expand="lg" className="navbar">
      <Container fluid>
        <Navbar.Brand href="/" className="brand">
          DESIGNER.AL
        </Navbar.Brand>

        <button className="navbar-toggler" type="button" onClick={toggleNavbar}>
          <span className="navbar-toggler-icon"></span>
        </button>

        <div className={`navbar-collapse ${isNavbarOpen ? "show" : ""}`}>
          <span className="close-btn" onClick={toggleNavbar}>
            Ã—
          </span>

          <Nav className="me-auto">
            <Nav.Link href="/about">About Us</Nav.Link>
            <Nav.Link href="/contact">Contact Us</Nav.Link>

            <NavDropdown title="Explore" id="navbarScrollingDropdown">
              <NavDropdown.Item href="/products">Fragrances</NavDropdown.Item>
              <NavDropdown.Item href="/products">Accessories</NavDropdown.Item>
              <NavDropdown.Item href="/products">Hair Care</NavDropdown.Item>
              <NavDropdown.Item href="/products">Makeup</NavDropdown.Item>
              <NavDropdown.Item href="/products">Sets</NavDropdown.Item>
            </NavDropdown>
          </Nav>

          {/* Search Form */}
          <Form className="d-flex search-container" onSubmit={handleSearch}>
            <SearchIcon className="icon" onClick={toggleSearch} />
            {isSearchOpen && (
              <input
                type="text"
                className="search-bar"
                value={searchQuery}
                onChange={(e) => setSearchQuery(e.target.value)}
                placeholder="Search for products..."
              />
            )}
            <div className="cart-icon">
              <ShoppingCartOutlinedIcon className="icon" />
              <span className="cart-count">0</span>
            </div>
          </Form>
        </div>
      </Container>

      {/* Products Grid */}
      {isSearchOpen && (
        <div className="products-grid">
          <div className="product-section">
            <div className="sidebar">
              <h2>Markat</h2>
              <ul>
                <li>MOSCHINO JEANS</li>
                <li>REPLAY</li>
                <li>GUESS</li>
                <li>HUGO</li>
                <li>Calvin Klein</li>
              </ul>
            </div>
            <div className="products">
              {products.map((product) => (
                <div key={product.id} className="product">
                  <img src={product.image} alt={product.name} />
                  <p>{product.name}</p>
                  <span>{product.price}</span>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}
    </Navbar>
  );
};

export default NavigationBar;
