import axios from 'axios';

export const addToCart = async (productId, quantity) => {
    if (!productId) {
        console.error('Invalid product ID');
        return;
    }

    try {
        console.log('Adding to cart:', { productId, quantity }); // Debug log
        const userInfo = JSON.parse(localStorage.getItem('userInfo'));
        if (!userInfo || !userInfo.id) {
            console.error('User not logged in');
            // Handle not logged in state (e.g., redirect to login)
            return;
        }

        const response = await axios.post(`http://localhost:5000/api/cart/${userInfo.id}/add`, {
            productId,
            quantity
        });
        console.log('Add to cart response:', response.data); // Debug log
        // Handle successful add to cart (e.g., update UI, show notification)
    } catch (error) {
        console.error('Error adding product to cart:', error);
        // ... error handling ...
    }
};
