import React, { useState } from "react";
import SearchIcon from "@mui/icons-material/Search";
import ShoppingCartOutlinedIcon from "@mui/icons-material/ShoppingCartOutlined";
import "./NavigationBar.css";
import { Container, Nav, Navbar, Form } from "react-bootstrap";

const NavigationBar = ({ showBanner, setShowBanner }) => {
  const [activeMenu, setActiveMenu] = useState(null); // to track hover over Femra/Meshkuj
  const [clickedMenu, setClickedMenu] = useState(null); // to handle click events
  const [hoverCategory, setHoverCategory] = useState(null); // to track hover over categories

  const handleMenuClick = (menu) => {
    if (clickedMenu === menu) {
      setClickedMenu(null);
      setShowBanner(true); // Show the banner when the black stripe is hidden
    } else {
      setClickedMenu(menu);
      setShowBanner(false); // Hide the banner when the black stripe is active
    }
  };

  return (
    <Navbar expand="lg" className="navbar">
      <Container fluid>
        <Navbar.Brand href="/" className="brand">FASHION&FRIENDS</Navbar.Brand>

        <Nav className="me-auto my-2 my-lg-0">
          <Nav.Link
            onMouseEnter={() => setActiveMenu("femra")}
            onMouseLeave={() => setActiveMenu(null)}
            onClick={() => handleMenuClick("femra")}
          >
            FEMRA
          </Nav.Link>
          <Nav.Link
            onMouseEnter={() => setActiveMenu("meshkuj")}
            onMouseLeave={() => setActiveMenu(null)}
            onClick={() => handleMenuClick("meshkuj")}
          >
            MESHKUJ
          </Nav.Link>
        </Nav>

        <Form className="d-flex">
          <SearchIcon className="icon" />
          <div className="cart-icon">
            <ShoppingCartOutlinedIcon className="icon" />
            <span>0</span>
          </div>
        </Form>

        {/* Black Stripe Menu */}
        <div
          className={`black-stripe ${clickedMenu ? "show" : ""}`}
          onMouseEnter={() => setHoverCategory("keepOpen")} // Keep black stripe open when hovering
          onMouseLeave={() => setHoverCategory(null)} // Close when mouse leaves black stripe and dropdown
        >
          <span
            onMouseEnter={() => setHoverCategory("fragrances")}
          >
            Fragrances
          </span>
          <span
            onMouseEnter={() => setHoverCategory("accessories")}
          >
            Accessories
          </span>
          <span
            onMouseEnter={() => setHoverCategory("haircare")}
          >
            Hair Care
          </span>
          <span
            onMouseEnter={() => setHoverCategory("new")}
          >
            New
          </span>
          <span
            onMouseEnter={() => setHoverCategory("sale")}
          >
            Sale
          </span>
        </div>

        {/* White Dropdown for Subcategories */}
        <div
          className={`dropdown-content ${hoverCategory ? "show" : ""}`}
          onMouseEnter={() => setHoverCategory("keepOpen")} // Keep dropdown open when hovering over it
          onMouseLeave={() => setHoverCategory(null)} // Close when leaving dropdown
        >
          {hoverCategory === "fragrances" && (
            <div className="dropdown-content-inner">
              <ul>
                <li><a href="/fragrances/eau-de-parfum">Eau de Parfum</a></li>
                <li><a href="/fragrances/body-mists">Body Mists</a></li>
                <li><a href="/fragrances/perfume-sets">Perfume Sets</a></li>
              </ul>
              <div className="dropdown-image">
                <img src="/path/to/perfume.jpg" alt="Perfume" />
              </div>
            </div>
          )}
          
          {hoverCategory === "accessories" && (
            <div className="dropdown-content-inner">
              <ul>
                <li><a href="/accessories/jewelry">Jewelry</a></li>
                <li><a href="/accessories/bags">Bags</a></li>
                <li><a href="/accessories/watches">Watches</a></li>
              </ul>
              <div className="dropdown-image">
                <img src="/path/to/accessories.jpg" alt="Accessories" />
              </div>
            </div>
          )}
          
          {/* Add more categories as needed */}
        </div>
      </Container>
    </Navbar>
  );
};

export default NavigationBar;
