import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import "./ShoppingCart.css";
import NavigationBar from '../NavigationBar/NavigationBar';
import Footer from "../Footer/Footer";

const ShoppingCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchCartItems();
  }, []);

  const fetchCartItems = async () => {
    try {
      const userId = localStorage.getItem("userId");
      const response = await axios.get(`/api/cart/${userId}`);
      setCartItems(response.data);
      setLoading(false);
    } catch (err) {
      setError("Failed to fetch cart items");
      setLoading(false);
    }
  };

  const handleRemoveItem = async (id) => {
    try {
      const userId = localStorage.getItem("userId");
      await axios.delete(`/api/cart/${userId}/item/${id}`);
      fetchCartItems();
    } catch (err) {
      setError("Failed to remove item");
    }
  };

  const handleUpdateQuantity = async (id, quantity) => {
    try {
      const userId = localStorage.getItem("userId");
      await axios.put(`/api/cart/${userId}/item/${id}`, { quantity });
      fetchCartItems();
    } catch (err) {
      setError("Failed to update quantity");
    }
  };

  const getTotalPrice = () => {
    return cartItems.reduce(
      (total, item) => total + item.price * item.quantity,
      0
    );
  };

  if (loading) return <div>Loading...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div>
      <NavigationBar />
      <div className="cart-page">
        <h1 className="cart-title">Your Cart</h1>
        {cartItems.length === 0 ? (
          <div className="empty-cart">Your cart is empty.</div>
        ) : (
          <>
            <div className="cart-items-grid">
              {cartItems.map((item) => (
                <div key={item._id} className="cart-item-card">
                  <img
                    src={item.image}
                    alt={item.name}
                    className="cart-item-image"
                  />
                  <div className="cart-item-details">
                    <h2 className="cart-item-name">{item.name}</h2>
                    <p className="cart-item-price">{item.price} Lek</p>
                    <div className="cart-item-quantity">
                      <button onClick={() => handleDecreaseQuantity(item.id)}>
                        -
                      </button>
                      <span>{item.quantity}</span>
                      <button onClick={() => handleIncreaseQuantity(item.id)}>
                        +
                      </button>
                    </div>
                    <button
                      className="remove-item-btn"
                      onClick={() => handleRemoveItem(item.id)}
                    >
                      Remove
                    </button>
                  </div>
                </div>
              ))}
            </div>
            <div className="cart-summary">
              <h2>Total: {getTotalPrice()} Lek</h2>
              <Link
                className="checkout-btn"
                to="/checkout"
                state={{ cartItems }}  // Pass cartItems to Checkout.js
              >
                Proceed to Checkout
              </Link>
            </div>
          </>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default ShoppingCart;
