import React, { useState, useEffect } from "react";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "./ShoppingCart.css";
import NavigationBar from "../NavigationBar/NavigationBar";
import Footer from "../Footer/Footer";
import { FaTrash, FaPlus, FaMinus } from 'react-icons/fa';

const ShoppingCart = () => {
  const [cartItems, setCartItems] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  const getUserId = () => {
    let userId = localStorage.getItem("userId");
    if (!userId) {
      userId = uuidv4();
      localStorage.setItem("userId", userId);
    }
    return userId;
  };

  useEffect(() => {
    fetchCartItems();
  }, []);

  const fetchCartItems = async () => {
    try {
      const userId = getUserId();
      console.log("Fetching cart for user:", userId);
      const response = await axios.get(
        `http://localhost:5000/api/cart/${userId}`
      );
      const items = response.data.items || [];

      // Fetch product details for each item
      const detailedItems = await Promise.all(
        items.map((item) => fetchProductDetails(item.productId, item.quantity))
      );

      setCartItems(detailedItems);
      setLoading(false);
    } catch (error) {
      console.error("Error fetching cart items:", error);
      setError(
        "Failed to fetch cart items: " +
          (error.response ? error.response.data.message : error.message)
      );
      setLoading(false);
    }
  };

  const fetchProductDetails = async (productId, quantity) => {
    try {
      const response = await axios.get(
        `http://localhost:5000/api/products/${productId}`
      ); // Update this URL to your actual product API endpoint
      const product = response.data;

      return {
        ...product,
        quantity, // Include the quantity from the cart
      };
    } catch (error) {
      console.error("Error fetching product details:", error);
      return null; // Return null if product details can't be fetched
    }
  };

  const handleRemoveItem = async (productId) => {
    try {
      const userId = getUserId();
      await axios.delete(
        `http://localhost:5000/api/cart/${userId}/item/${productId}`
      );
      fetchCartItems(); // Refresh cart
    } catch (err) {
      setError("Failed to remove item");
    }
  };

  const updateCartItem = async (productId, newQuantity) => {
    try {
      const userId = getUserId();
      const quantity = parseInt(newQuantity, 10);

      if (quantity < 1) {
        console.error("Quantity must be at least 1");
        return;
      }

      await axios.put(
        `http://localhost:5000/api/cart/${userId}/item/${productId}`,
        { quantity }
      );
      fetchCartItems(); // Refresh cart after update
    } catch (error) {
      console.error(
        "Error updating cart item:",
        error.response?.data || error.message
      );
    }
  };

  const clearCart = async () => {
    try {
      const userId = getUserId();
      await axios.delete(`http://localhost:5000/api/cart/${userId}`);
      fetchCartItems();
    } catch (err) {
      setError("Failed to clear cart");
    }
  };

  const handleQuantityChange = async (productId, newQuantity) => {
    if (newQuantity < 1) return;
    try {
      await updateCartItem(productId, newQuantity);
      fetchCartItems();
    } catch (error) {
      console.error("Error updating quantity:", error);
      setError("Failed to update quantity");
    }
  };

  const handleCheckout = () => {
    if (cartItems.length === 0) {
      alert("Your cart is empty!");
      return;
    }
    navigate('/checkout', { state: { cartItems, totalPrice: getTotalPrice() } });
  };

  const getTotalPrice = () => {
    return cartItems.reduce(
      (total, item) => total + item.price * item.quantity,
      0
    );
  };

  if (loading) return <div>Loading... Please wait.</div>;
  if (error) return <div>Error: {error}</div>;

  return (
    <div>
      <NavigationBar />
      <div className="cart-page">
        <h1 className="cart-title">Your Shopping Cart</h1>
        {cartItems.length === 0 ? (
          <div className="empty-cart">
            <p>Your cart is empty.</p>
            <Link to="/products" className="continue-shopping-btn">
              Continue Shopping
            </Link>
          </div>
        ) : (
          <div className="cart-content">
            <div className="cart-items">
              {cartItems.map((item) => (
                <div key={item._id} className="cart-item">
                  <img src={item.image} alt={item.name} className="cart-item-image" />
                  <div className="cart-item-details">
                    <h3>{item.name}</h3>
                    <p className="cart-item-price">{item.price} Lek</p>
                  </div>
                  <div className="cart-item-quantity">
                    <button onClick={() => handleQuantityChange(item._id, item.quantity - 1)}>
                      <FaMinus />
                    </button>
                    <input
                      type="number"
                      min="1"
                      value={item.quantity}
                      onChange={(e) => handleQuantityChange(item._id, parseInt(e.target.value))}
                    />
                    <button onClick={() => handleQuantityChange(item._id, item.quantity + 1)}>
                      <FaPlus />
                    </button>
                  </div>
                  <p className="cart-item-subtotal">
                    {(item.price * item.quantity).toFixed(2).toLocaleString()} Lek
                  </p>
                  <button className="remove-item" onClick={() => handleRemoveItem(item._id)}>
                    <FaTrash />
                  </button>
                </div>
              ))}
            </div>
            <div className="cart-summary">
              <h2>Order Summary</h2>
              <div className="summary-row">
                <span>Subtotal</span>
                <span>{getTotalPrice().toLocaleString()} Lek</span>
              </div>
              <div className="summary-row">
                <span>Shipping</span>
                <span>0.00 Lek</span>
              </div>
              <div className="summary-row total">
                <span>Total</span>
                <span>{getTotalPrice().toLocaleString()} Lek</span>
              </div>
              <button onClick={handleCheckout} className="checkout-btn">
                Proceed to Checkout
              </button>
              <button className="clear-cart-btn" onClick={clearCart}>
                Clear Cart
              </button>
              <Link to="/products" className="continue-shopping-btn">
                Continue Shopping
              </Link>
            </div>
          </div>
        )}
      </div>
      <Footer />
    </div>
  );
};

export default ShoppingCart;
