import React, { useState } from 'react'
import { Container, Form, Button, Alert } from 'react-bootstrap'
import axios from 'axios'
import { useNavigate, Link } from 'react-router-dom'
import NavigationBar from '../NavigationBar/NavigationBar'
import Footer from '../Footer/Footer'
import './Register.css'

const Register = () => {
    const navigate = useNavigate()
    const [newUser, setNewUser] = useState({
        username: "",
        email: "",
        password: ""
    })
    const [error, setError] = useState("")
    const [alert, setAlert] = useState("")

    const handleChange = (e) => {
        setNewUser({ ...newUser, [e.target.name]: e.target.value })
    }

    const handleSubmit = async (e) => {
        e.preventDefault()
        if (!newUser.username) {
            setError('Username should not be empty');
            setAlert("danger")
            return;
        }
        if (!newUser.email) {
            setError('Email should not be empty');
            setAlert("danger")
            return;
        }
        if (newUser.password.length < 8) {
            setError('Password should have at least 8 characters!');
            setAlert("danger")
            return;
        }
        await axios.post('http://localhost:5000/register/', newUser)
            .then((res) => {
                console.log(res.data)
                navigate('/login')
            })
            .catch(err => {
                console.log("User not added " + err)
                setAlert('danger');
                setError("User not created")
            })
    }

    return (
        <div className="auth-register-page">
            <NavigationBar />
      <div className="auth-register-container">
        <form className="auth-register-form" onSubmit={handleSubmit}>
          <div className="auth-register-form-group">
            <label className="auth-register-form-label" htmlFor="username">Username</label>
            <input
              className="auth-register-form-control"
              id="username"
              type="text"
              name="username"
              value={newUser.username}
              onChange={handleChange}
            />
          </div>
          <div className="auth-register-form-group">
            <label className="auth-register-form-label" htmlFor="email">Email</label>
            <input
              className="auth-register-form-control"
              id="email"
              type="email"
              name="email"
              value={newUser.email}
              onChange={handleChange}
            />
          </div>
          <div className="auth-register-form-group">
            <label className="auth-register-form-label" htmlFor="password">Password</label>
            <input
              className="auth-register-form-control"
              id="password"
              type="password"
              name="password"
              value={newUser.password}
              onChange={handleChange}
            />
          </div>
          <button className="auth-register-submit-btn" type="submit">Register</button>
          <div className="auth-register-login-link">
            Already have an account? <Link to="/login">Login</Link>
          </div>
        </form>
      </div>
    </div>
    )
}

export default Register
